#!/usr/bin/env node

/**
 * ANNIHILATION-AGENT-77: Unused _error Destroyer
 * 
 * Converts ALL catch blocks with unused `_error` parameters to anonymous catches.
 * Only converts if _error is TRULY unused in the catch block.
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

// Find all TypeScript and Svelte files with catch (_error) patterns
function findFilesWithCatchError() {
    try {
        const command = `grep -r -l "catch\\s*(\\s*_error\\s*)" --include="*.ts" --include="*.svelte" src/ 2>/dev/null || true`;
        const output = execSync(command, { encoding: 'utf8' });
        return output.trim().split('\n').filter(line => line.length > 0);
    } catch (error) {
        console.warn('Error finding files:', error.message);
        return [];
    }
}

// Check if _error is actually used within a catch block
function isErrorUsedInCatchBlock(catchBlockContent) {
    // Remove the catch declaration line itself to avoid false positives
    const blockWithoutDeclaration = catchBlockContent.replace(/catch\s*\(\s*_error\s*\)\s*{/, '');

    // Look for actual usage of _error (not just in comments)
    const usagePatterns = [
        /_error\./,           // _error.message, _error.code, etc.
        /_error\[/,           // _error['property']
        /_error\s*;/,         // _error;
        /_error\s*\)/,        // function(_error)
        /_error\s*,/,         // _error, other
        /_error\s*\}/,        // return { _error }
        /_error\s*:/,         // { _error: value }
        /`.*\$\{_error\}/,    // Template literal ${_error}
        /['"][^'"]*\$\{_error\}/,  // String with ${_error}
        /console\.[^(]*\([^)]*_error/,  // console.log(_error)
        /throw\s+_error/,     // throw _error
        /return\s+_error/,    // return _error
        /=\s*_error/,         // variable = _error
    ];

    return usagePatterns.some(pattern => pattern.test(blockWithoutDeclaration));
}

// Extract catch blocks from file content
function extractCatchBlocks(content) {
    const catchBlocks = [];
    const regex = /catch\s*\(\s*_error\s*\)\s*{/g;
    let match;

    while ((match = regex.exec(content)) !== null) {
        const startPos = match.index;
        const openBracePos = content.indexOf('{', startPos);

        // Find the matching closing brace
        let braceCount = 1;
        let endPos = openBracePos + 1;

        while (endPos < content.length && braceCount > 0) {
            if (content[endPos] === '{') braceCount++;
            else if (content[endPos] === '}') braceCount--;
            endPos++;
        }

        if (braceCount === 0) {
            const fullBlock = content.substring(startPos, endPos);
            catchBlocks.push({
                fullBlock,
                startPos,
                endPos,
                isUsed: isErrorUsedInCatchBlock(fullBlock)
            });
        }
    }

    return catchBlocks;
}

// Convert unused _error parameters to anonymous catches
function convertFile(filePath) {
    console.log(`Analyzing: ${filePath}`);

    let content;
    try {
        content = fs.readFileSync(filePath, 'utf8');
    } catch (error) {
        console.warn(`Could not read ${filePath}:`, error.message);
        return { converted: 0, skipped: 0 };
    }

    const catchBlocks = extractCatchBlocks(content);
    if (catchBlocks.length === 0) {
        return { converted: 0, skipped: 0 };
    }

    let converted = 0;
    let skipped = 0;
    let newContent = content;

    // Process blocks in reverse order to maintain correct positions
    catchBlocks.reverse().forEach(block => {
        if (block.isUsed) {
            console.log(`  SKIPPED: _error is used in catch block at position ${block.startPos}`);
            skipped++;
        } else {
            console.log(`  CONVERTING: unused _error at position ${block.startPos}`);
            const before = newContent.substring(0, block.startPos);
            const after = newContent.substring(block.endPos);
            const convertedBlock = block.fullBlock.replace(/catch\s*\(\s*_error\s*\)\s*{/, 'catch {');
            newContent = before + convertedBlock + after;
            converted++;
        }
    });

    if (converted > 0) {
        try {
            fs.writeFileSync(filePath, newContent, 'utf8');
            console.log(`âœ… ${filePath}: ${converted} converted, ${skipped} skipped`);
        } catch (error) {
            console.error(`âŒ Could not write ${filePath}:`, error.message);
        }
    } else {
        console.log(`âšª ${filePath}: ${skipped} skipped (all _error parameters are used)`);
    }

    return { converted, skipped };
}

// Main execution
function main() {
    console.log('ðŸš€ ANNIHILATION-AGENT-77: Starting unused _error destruction...\n');

    const files = findFilesWithCatchError();

    if (files.length === 0) {
        console.log('No files found with catch (_error) patterns.');
        return;
    }

    console.log(`Found ${files.length} files with catch (_error) patterns:\n`);

    let totalConverted = 0;
    let totalSkipped = 0;

    files.forEach(file => {
        const result = convertFile(file);
        totalConverted += result.converted;
        totalSkipped += result.skipped;
    });

    console.log('\n' + '='.repeat(60));
    console.log(`ðŸŽ¯ DESTRUCTION COMPLETE!`);
    console.log(`ðŸ“Š Results:`);
    console.log(`   âœ… Converted: ${totalConverted} catch blocks`);
    console.log(`   âšª Skipped: ${totalSkipped} catch blocks (error parameter is used)`);
    console.log(`   ðŸ“ Files processed: ${files.length}`);

    if (totalConverted > 0) {
        console.log(`\nðŸ”§ Run 'npm run check' to verify TypeScript compilation.`);
    }
}

main();

/**
 * Analytics Events API Endpoint
 *
 * Receives batched analytics events from the client and stores them in the database.
 * Handles session management and user identification.
 */

import { json } from '@sveltejs/kit';
import type { RequestHandler } from './$types';
import { db } from '$lib/core/db';
import type { AnalyticsEvent as _AnalyticsEvent } from '$lib/types/analytics';

// Incoming event structure from client (flexible)
interface ClientAnalyticsEvent {
	session_id: string;
	event_name?: string;
	name?: string;
	event_type?: string;
	user_id?: string;
	template_id?: string;
	funnel_id?: string;
	campaign_id?: string;
	variation_id?: string;
	timestamp?: Date | string;
	page_url?: string;
	event_properties?: Record<string, unknown>;
	properties?: Record<string, unknown>;
}

interface EventBatch {
	session_data: {
		session_id: string;
		user_id?: string;
		fingerprint?: string;
		ip_address?: string;
		user_agent?: string;
		referrer?: string;
		utm_source?: string;
		utm_medium?: string;
		utm_campaign?: string;
		landing_page?: string;
	};
	events: ClientAnalyticsEvent[];
}

export const POST: RequestHandler = async ({ request, getClientAddress }) => {
	try {
		const { session_data, events }: EventBatch = await request.json();

		if (!session_data?.session_id || !Array.isArray(events)) {
			return json(
				{
					success: false,
					error: 'Invalid request format'
				},
				{ status: 400 }
			);
		}

		// Get client IP for geolocation (if not provided)
		const clientIP = session_data.ip_address || getClientAddress();

		// Validate user_id exists if provided
		let validatedUserId: string | null = null;
		if (session_data.user_id) {
			const userExists = await db.user.findUnique({
				where: { id: session_data.user_id },
				select: { id: true }
			});
			validatedUserId = userExists ? session_data.user_id : null;
		}

		// Ensure analytics session exists using new consolidated schema
		await db.analytics_session.upsert({
			where: {
				session_id: session_data.session_id
			},
			create: {
				session_id: session_data.session_id,
				user_id: validatedUserId,
				created_at: new Date(),
				updated_at: new Date(),
				utm_source: session_data.utm_source,
				utm_medium: session_data.utm_medium,
				utm_campaign: session_data.utm_campaign,
				landing_page: session_data.landing_page,
				referrer: session_data.referrer,
				device_data: {
					ip_address: clientIP,
					user_agent: session_data.user_agent,
					fingerprint: session_data.fingerprint
				},
				session_metrics: {
					events_count: events.length,
					page_views: events.filter((e) => (e.event_name || e.name) === 'page_view').length,
					conversion_count: events.filter((e) => (e.event_name || e.name) === 'conversion').length
				},
				funnel_progress: {}
			},
			update: {
				user_id: validatedUserId || undefined,
				updated_at: new Date(),
				session_metrics: {
					events_count: { increment: events.length },
					page_views: {
						increment: events.filter((e) => (e.event_name || e.name) === 'page_view').length
					},
					conversion_count: {
						increment: events.filter((e) => (e.event_name || e.name) === 'conversion').length
					}
				}
			}
		});

		// Validate template_ids exist if provided
		const templateIds = [
			...new Set(events.map((e) => e.template_id).filter((id): id is string => Boolean(id)))
		];
		const validTemplateIds = new Set();
		if (templateIds.length > 0) {
			const existingTemplates = await db.template.findMany({
				where: { id: { in: templateIds } },
				select: { id: true }
			});
			existingTemplates.forEach((t) => validTemplateIds.add(t.id));
		}

		// Helper function to safely stringify properties (handle circular references)
		const safeStringify = (obj: unknown): string => {
			if (!obj || typeof obj !== 'object') return String(obj);

			const seen = new WeakSet();
			return JSON.stringify(obj, (key, value) => {
				// Skip DOM elements (only available in browser)
				if (typeof HTMLElement !== 'undefined' && value instanceof HTMLElement) {
					return '[HTMLElement]';
				}
				// Skip Window/Document objects
				if (value === globalThis || value === globalThis.document) {
					return '[Window/Document]';
				}
				// Handle circular references
				if (typeof value === 'object' && value !== null) {
					if (seen.has(value)) {
						return '[Circular]';
					}
					seen.add(value);
				}
				// Skip functions
				if (typeof value === 'function') {
					return '[Function]';
				}
				return value;
			});
		};

		// Process and store events using new consolidated schema with JSONB properties
		const eventsToCreate = [];

		for (const event of events) {
			// Handle event properties (merge both properties and event_properties)
			const properties = {
				...(event.properties || {}),
				...(event.event_properties || {}),
				page_url: event.page_url
			};

			// Clean properties to prevent circular references and large objects
			const cleanedProperties: Record<string, unknown> = {};
			for (const [key, value] of Object.entries(properties)) {
				if (value !== undefined && value !== null) {
					try {
						if (typeof value === 'object') {
							// For objects, stringify and parse to ensure they're JSON-safe
							cleanedProperties[key] = JSON.parse(safeStringify(value));
						} else {
							cleanedProperties[key] = value;
						}
					} catch {
						console.warn(`Could not analyze template`, err);
						cleanedProperties[key] = '[Serialization Error]';
					}
				}
			}

			// Determine event type
			const eventName = event.event_name || event.name || 'unknown';
			let eventType: 'pageview' | 'interaction' | 'conversion' | 'funnel' | 'campaign' =
				'interaction';

			if (eventName === 'page_view' || eventName.includes('viewed')) {
				eventType = 'pageview';
			} else if (
				eventName.includes('conversion') ||
				eventName === 'template_used' ||
				eventName === 'auth_completed'
			) {
				eventType = 'conversion';
			} else if (event.funnel_id) {
				eventType = 'funnel';
			} else if (event.campaign_id) {
				eventType = 'campaign';
			}

			eventsToCreate.push({
				session_id: session_data.session_id,
				user_id: event.user_id || validatedUserId || null,
				timestamp: event.timestamp ? new Date(event.timestamp) : new Date(),
				name: eventName,
				event_type: eventType,
				template_id:
					event.template_id && validTemplateIds.has(event.template_id) ? event.template_id : null,
				funnel_step: event.funnel_id ? 1 : null, // Could be enhanced based on event data
				experiment_id: event.variation_id || event.campaign_id || null,
				properties: cleanedProperties,
				computed_metrics: {}
			});
		}

		// Batch insert events
		if (eventsToCreate.length > 0) {
			await db.analytics_event.createMany({
				data: eventsToCreate,
				skipDuplicates: true
			});
		}

		// Update session end time and check for conversions
		const hasConversion = events.some(
			(e) => e.event_name === 'template_used' || e.event_name === 'auth_completed'
		);

		// Note: Conversion tracking would require additional user_session schema fields
		// For now, we track conversions through analyticsevent records

		return json({
			success: true,
			events_processed: events.length,
			session_id: session_data.session_id
		});
	} catch {
		console.error('Error occurred');

		return json(
			{
				success: false,
				error: 'Failed to process analytics events'
			},
			{ status: 500 }
		);
	}
};

// Optional: GET endpoint for debugging (remove in production)
export const GET: RequestHandler = async ({ url }) => {
	const sessionId = url.searchParams.get('session_id');

	if (!sessionId) {
		return json(
			{
				success: false,
				error: 'session_id required'
			},
			{ status: 400 }
		);
	}

	try {
		const session = await db.analytics_session.findUnique({
			where: { session_id: sessionId }
		});

		if (!session) {
			return json(
				{
					success: false,
					error: 'Session not found'
				},
				{ status: 404 }
			);
		}

		// Get analytics events for this session from consolidated analyticsevent table
		const analyticsEvents = await db.analytics_event.findMany({
			where: { session_id: sessionId },
			orderBy: { timestamp: 'desc' },
			take: 50
		});

		return json({
			success: true,
			session: {
				...session,
				// Convert JSONB fields to objects for response
				device_data: session.device_data || {},
				session_metrics: session.session_metrics || {},
				funnel_progress: session.funnel_progress || {}
			},
			analyticsevents: analyticsEvents.map((event) => ({
				...event,
				properties: event.properties || {},
				computed_metrics: event.computed_metrics || {}
			})),
			events_count: analyticsEvents.length
		});
	} catch {
		console.error('Error occurred');

		return json(
			{
				success: false,
				error: 'Failed to fetch session data'
			},
			{ status: 500 }
		);
	}
};

import { json } from '@sveltejs/kit';
import {
	analyzeCivicInformationCascades,
	storeCascadeAnalysis
} from '$lib/core/server/percolation-engine';
import { db } from '$lib/core/db';
import type { PercolationData } from '$lib/types/analytics';
import type { RequestHandler } from './$types';
import type { PercolationAnalysis } from '$lib/types/any-replacements';

export const GET: RequestHandler = async ({ url, locals }) => {
	try {
		// Run percolation-style connectivity analysis on the civic information network
		const analysis = await analyzeCivicInformationCascades();

		// Store results for historical tracking
		await storeCascadeAnalysis(analysis);

		// Optional: Log analytics event for percolation analysis view
		if (locals.user?.id) {
			try {
				await db.analytics_event.create({
					data: {
						session_id: crypto.randomUUID(),
						user_id: locals.user.id,
						timestamp: new Date(),
						name: 'percolation_analysis_viewed',
						event_type: 'interaction',
						properties: {
							cascade_status: analysis.cascade_potential,
							network_health: analysis.max_flow_capacity > 10 ? 'strong' : 'weak',
							critical_node_count: analysis.critical_nodes?.length || 0
						},
						computed_metrics: {}
					}
				});
			} catch {
				// Ignore analytics errors
			}
		}

		return json({
			success: true,
			analysis,
			timestamp: new Date().toISOString(),
			interpretation: {
				cascade_status: analysis.cascade_potential,
				network_health: analysis.max_flow_capacity > 10 ? 'strong' : 'weak',
				critical_node_count: analysis.critical_nodes.length,
				bottleneck_severity: analysis.bottleneck_edges.length > 5 ? 'high' : 'moderate',
				recommendation: getRecommendation(analysis)
			}
		});
	} catch {
		console.error('Error occurred');

		return json(
			{
				success: false,
				error: 'Failed to analyze information cascades',
				details: error instanceof Error ? error.message : 'Unknown error'
			},
			{ status: 500 }
		);
	}
};

function getRecommendation(analysis: PercolationAnalysis): string {
	if (analysis.cascade_potential === 'supercritical') {
		return 'Network in optimal state for viral spread. Focus on quality content creation.';
	} else if (analysis.cascade_potential === 'critical') {
		return 'Network approaching viral threshold. Target critical nodes for maximum impact.';
	} else {
		return 'Network below viral threshold. Focus on connecting isolated communities.';
	}
}

export const POST: RequestHandler = async ({ request }) => {
	try {
		const { action, parameters } = await request.json();

		if (action === 'refresh') {
			// Force refresh of network analysis
			const analysis = await analyzeCivicInformationCascades();
			await storeCascadeAnalysis(analysis);

			return json({
				success: true,
				message: 'Network analysis refreshed',
				analysis
			});
		}

		return json(
			{
				success: false,
				error: 'Invalid action'
			},
			{ status: 400 }
		);
	} catch {
		return json(
			{
				success: false,
				error: 'Failed to process request'
			},
			{ status: 500 }
		);
	}
};

/**
 * CWC (Communicating With Congress) Submission Endpoint
 *
 * Handles verified template submission to Congressional offices
 * Called by N8N workflow after verification and consensus stages
 */

import { json } from '@sveltejs/kit';
import type { RequestHandler } from './$types';
import { cwcClient } from '$lib/core/congress/cwc-client';
import { CWCGenerator } from '$lib/core/congress/cwc-generator';
import { db } from '$lib/core/db';

export const POST: RequestHandler = async ({ request, url }) => {
	try {
		// Verify webhook secret if provided
		const webhookSecret = request.headers.get('x-webhook-secret');
		const expectedSecret = process.env.N8N_WEBHOOK_SECRET;

		if (expectedSecret && webhookSecret !== expectedSecret) {
			return json({ error: 'Invalid webhook secret' }, { status: 401 });
		}

		const body = await request.json();
		const {
			templateId,
			verification,
			template: templateData,
			user: userData,
			recipients = []
		} = body;

		// Validate required fields
		if (!templateId || !templateData || !userData) {
			return json(
				{
					error: 'Missing required fields',
					required: ['templateId', 'template', 'user']
				},
				{ status: 400 }
			);
		}

		// Fetch template from database if not provided fully
		let template = templateData;
		if (templateId && (!template.subject || !template.body)) {
			const dbTemplate = await db.template.findUnique({
				where: { id: templateId },
				include: {
					user: true
				}
			});

			if (!dbTemplate) {
				return json({ error: 'Template not found' }, { status: 404 });
			}

			template = {
				...dbTemplate,
				...template,
				// Use corrected content if available from verification fields on template
				subject:
					dbTemplate.corrected_subject ||
					verification?.corrections?.subject ||
					template.subject ||
					dbTemplate.subject,
				body:
					dbTemplate.corrected_body ||
					verification?.corrections?.body ||
					template.body ||
					dbTemplate.message_body
			};
		}

		// Prepare user data
		const user = {
			id: userData.id || 'n8n-user',
			name: userData.name || userData.userName,
			email: userData.email || userData.userEmail,
			phone: userData.phone || '',
			street: userData.address || userData.userAddress || '',
			city: userData.city || '',
			state: userData.state || '',
			zip: userData.zip || userData.userZip || ''
		};

		// If recipients not provided, look them up based on user address
		let targetRecipients = recipients;
		if (targetRecipients.length === 0 && user.zip) {
			// Look up representatives based on zip code
			const { addressLookup } = await import('$lib/core/congress/address-lookup');
			const reps = await addressLookup(user.zip);
			targetRecipients = reps.map(
				(rep: {
					bioguideId: string;
					name: string;
					role: string;
					state: string;
					district?: string;
					party?: string;
				}) => ({
					bioguideId: rep.bioguideId,
					name: rep.name,
					chamber: rep.role.includes('Senator') ? 'senate' : 'house',
					officeCode: rep.bioguideId,
					state: rep.state,
					district: rep.district || '00',
					party: rep.party || 'Unknown'
				})
			);
		}

		if (targetRecipients.length === 0) {
			return json(
				{
					error: 'No recipients specified and unable to determine from address',
					hint: 'Provide recipients array or valid user address/zip'
				},
				{ status: 400 }
			);
		}

		// Track submissions
		const submissions = [];
		const errors = [];

		// Submit to each recipient
		for (const recipient of targetRecipients) {
			try {
				let result;

				if (recipient.chamber === 'senate') {
					result = await cwcClient.submitToSenate(template, user, recipient, template.body);
				} else {
					result = await cwcClient.submitToHouse(template, user, recipient, template.body);
				}

				if (result.success) {
					submissions.push({
						recipient: recipient.name,
						chamber: recipient.chamber,
						messageId: result.messageId,
						confirmationNumber: result.confirmationNumber,
						status: result.status
					});

					// Store delivery record
					await db.template_campaign.create({
						data: {
							id: `cwc_${templateId}_${user.id}_${recipient.bioguideId}_${Date.now()}`,
							template_id: templateId,
							user_id: user.id,
							delivery_type: 'cwc',
							recipient_id: recipient.bioguideId,
							cwc_delivery_id: result.messageId,
							status: 'delivered',
							sent_at: new Date(),
							delivered_at: new Date(),
							metadata: {
								recipient_name: recipient.name,
								chamber: recipient.chamber,
								confirmation_number: result.confirmationNumber
							}
						}
					});
				} else {
					errors.push({
						recipient: recipient.name,
						error: result.error || 'Submission failed'
					});
				}
			} catch {
				console.error('Error occurred');
				errors.push({
					recipient: recipient.name,
					error: error instanceof Error ? error.message : 'Unknown error'
				});
			}
		}

		// Update template usage tracking
		if (submissions.length > 0) {
			// Note: Template update fields not available in current Prisma client
			// TODO: Regenerate Prisma client after schema updates
			console.log(`Template ${templateId} used ${submissions.length} times`);
		}

		// Prepare response
		const response = {
			success: submissions.length > 0,
			templateId,
			submissionCount: submissions.length,
			submissions,
			errors: errors.length > 0 ? errors : undefined,
			confirmationNumber: submissions[0]?.confirmationNumber, // Primary confirmation for N8N
			timestamp: new Date().toISOString()
		};

		// If all submissions failed, return error
		if (submissions.length === 0 && errors.length > 0) {
			return json(
				{
					...response,
					error: 'All submissions failed',
					details: errors
				},
				{ status: 500 }
			);
		}

		return json(response);
	} catch {
		console.error('Error occurred');
		return json(
			{
				success: false,
				error: 'CWC submission failed',
				details: error instanceof Error ? error.message : 'Unknown error'
			},
			{ status: 500 }
		);
	}
};

