			}
		});

		// Include template scopes - handle if table doesn't exist
		let scopes: UnknownRecord[] = [];
		try {
			// Check if template_scope table exists in the db schema
			if ('template_scope' in db) {
				scopes = await (
					db as unknown as {
						template_scope: { findMany: (params: unknown) => Promise<UnknownRecord[]> };
					}
				).template_scope.findMany({
					where: { template_id: { in: dbTemplates.map((t) => t.id) } }
				});
			}
		} catch (errorscopeError) {
			// template_scope table might not exist, continue without scopes
			console.warn('template_scope table not found, continuing without scopes');
		}

		const idToScope = new Map(scopes.map((s) => [s.template_id, s]));

		const formattedTemplates = dbTemplates.map((template) => ({
			id: template.id,
			slug: template.slug,
			title: template.title,
			description: template.description,
			category: template.category,
			type: template.type,
			deliveryMethod: template.deliveryMethod,
			subject: template.subject,
			message_body: template.message_body,
			preview: template.preview,
			metrics: template.metrics,
			delivery_config: template.delivery_config,
			cwc_config: template.cwc_config, // Was missing from API response
			recipient_config: template.recipient_config,
			campaign_id: template.campaign_id, // Was missing from API response
			status: template.status, // Was missing from API response
			is_public: template.is_public,
			// Usage tracking fields
			send_count: template.send_count,
			last_sent_at: template.last_sent_at,
			// Geographic scope fields - were missing from API response
			applicable_countries: template.applicable_countries,
			jurisdiction_level: template.jurisdiction_level,
			specific_locations: template.specific_locations,
			// Optional scope from separate table
			scope: idToScope.get(template.id) || null,
			recipientEmails: extractRecipientEmails(
				typeof template.recipient_config === 'string'
					? JSON.parse(template.recipient_config)
					: template.recipient_config
			)
		}));

		const response: ApiResponse = {
			success: true,
			data: formattedTemplates
		};

		return json(response);
	} catch {
		console.error('Error occurred');

		const response: ApiResponse = {
			success: false,
			error: createApiError('server', 'SERVER_DATABASE', 'Failed to fetch templates')
		};

		return json(response, { status: 500 });
	}
};

export const POST: RequestHandler = async ({ request, locals }) => {
	try {
		// Parse request body
		let requestData: unknown;
		try {
			requestData = await request.json();
		} catch (errorparseError) {
			const response: ApiResponse = {
				success: false,
				error: createApiError(
					'validation',
					'VALIDATION_INVALID_FORMAT',
					'Invalid JSON in request body'
				)
			};
			return json(response, { status: 400 });
		}

		// Validate template data
		const validation = validateTemplateData(requestData);
		if (!validation.isValid) {
			const response: ApiResponse = {
				success: false,
				errors: validation.errors
			};
			return json(response, { status: 400 });
		}

		const validData = validation.validData!;
		const user = locals.user;

		if (user) {
			// Authenticated user - save to database
			try {
				// Check for duplicate slug - validData.title is guaranteed to exist from validation
				const slug = validData.title
					.toLowerCase()
					.replace(/[^a-z0-9\s-]/g, '')
					.replace(/\s+/g, '-')
					.substring(0, 50);

				const existingTemplate = await db.template.findUnique({
					where: { slug }
				});

				if (existingTemplate) {
					const response: ApiResponse = {
						success: false,
						error: createValidationError(
							'title',
							'VALIDATION_DUPLICATE',
							'A template with a similar title already exists. Please choose a different title.'
						)
					};
					return json(response, { status: 400 });
				}

				const newTemplate = await db.template.create({
					data: {
						title: validData.title,
						description: validData.description || '',
						message_body: validData.message_body,
						category: validData.category || 'General',
						type: validData.type,
						deliveryMethod: validData.deliveryMethod,
						subject: validData.subject,
						preview: validData.preview,
						delivery_config: validData.delivery_config || {},
						cwc_config: validData.cwc_config || {},
						recipient_config: validData.recipient_config || {},
						metrics: validData.metrics || {},
						status: validData.status || 'draft',
						is_public: validData.is_public || false,
						slug,
						userId: user.id,
						// Consolidated verification fields with defaults
						verification_status: 'pending',
						country_code: 'US',
						reputation_applied: false
					}
				});

				// Set verification fields for congressional templates (deliveryMethod === 'cwc')
				if (validData.deliveryMethod === 'cwc') {
					try {
						// Update template with initial verification status
						await db.template.update({
							where: { id: newTemplate.id },
							data: {
								verification_status: 'pending',
								country_code: 'US', // TODO: Extract from user profile
								quality_score: 0,
								reputation_applied: false
							}
						});

						// Trigger moderation pipeline via webhook
						await triggerModerationPipeline(newTemplate.id);
						console.log(`Set verification status for congressional template ${newTemplate.id}`);
					} catch (errorverificationError) {
						console.error('Failed to set template verification status:', verificationError);
						// Don't fail the template creation, just log the error
					}
				}

				const response: ApiResponse = {
					success: true,
					data: { template: newTemplate }
				};

				return json(response);
			} catch (errordbError) {
				console.error('Database error creating template:', dbError);

				const response: ApiResponse = {
					success: false,
					error: createApiError('server', 'SERVER_DATABASE', 'Failed to save template to database')
				};

				return json(response, { status: 500 });
			}
		} else {
			// Guest user - return the template data with a temporary ID for client-side storage
			const guestTemplate = {
				...validData,
				id: `guest-${Date.now()}`,
				slug: validData.title
					.toLowerCase()
					.replace(/[^a-z0-9\s-]/g, '')
					.replace(/\s+/g, '-')
					.substring(0, 50),
				created_at: new Date().toISOString(),
				userId: null
			};

			const response: ApiResponse = {
				success: true,
				data: { template: guestTemplate }
			};

			return json(response);
		}
	} catch {
		console.error('Error occurred');

		const response: ApiResponse = {
			success: false,
			error: createApiError('server', 'SERVER_INTERNAL', 'An unexpected error occurred')
		};

		return json(response, { status: 500 });
	}
};

/**
 * Trigger moderation pipeline for a template
 */
async function triggerModerationPipeline(templateId: string) {
	try {
		// Call our own moderation webhook with the template ID
		const response = await fetch(
			`${process.env.ORIGIN || 'http://localhost:5173'}/api/webhooks/template-moderation`,
			{
				method: 'POST',
				headers: {
					'Content-Type': 'application/json',
					'x-webhook-secret': process.env.N8N_WEBHOOK_SECRET || 'demo-secret'
				},
				body: JSON.stringify({
					template_id: templateId,
					source: 'template-creation',
					timestamp: new Date().toISOString()
				})
			}
		);

		if (!response.ok) {
			throw new Error(`Webhook failed with status ${response.status}`);
		}

		const result = await response.json();
		console.log(`Moderation pipeline triggered for template ${templateId}:`, result);

		return result;
	} catch {
		console.error('Error occurred');
		// Don't throw - we don't want to fail template creation if moderation fails to trigger
		return { success: false, error: error instanceof Error ? error.message : 'Unknown error' };
	}
}

import { json, error } from '@sveltejs/kit';
import { db } from '$lib/core/db';
import { updateTemplateDistrictMetrics } from '$lib/core/server/district-metrics';
import { extractRecipientEmails } from '$lib/types/templateConfig';
import type { RequestHandler } from './$types';
import type { TemplateUpdateData } from '$lib/types/api';
import type { UnknownRecord } from '$lib/types/any-replacements';

export const GET: RequestHandler = async ({ params }) => {
	try {
		const templateId = params.id;

		const template = await db.template.findUnique({
			where: { id: templateId }
		});

		if (!template) {
			return error(404, 'Template not found');
		}

		// Update district metrics for congressional templates
		if (template.deliveryMethod === 'cwc') {
			await updateTemplateDistrictMetrics(templateId);

			// Refetch template to get updated metrics
			const updatedTemplate = await db.template.findUnique({
				where: { id: templateId }
			});

			if (updatedTemplate) {
				template.metrics = updatedTemplate.metrics;
			}
		}

		const formattedTemplate = {
			id: template.id,
			title: template.title,
			description: template.description,
			category: template.category,
			type: template.type,
			deliveryMethod: template.deliveryMethod,
			subject: template.subject,
			preview: template.preview,
			message_body: template.message_body,
			metrics: template.metrics,
			delivery_config: template.delivery_config,
			recipient_config: template.recipient_config,
			is_public: template.is_public,
			recipientEmails: extractRecipientEmails(template.recipient_config)
		};

		return json(formattedTemplate);
	} catch {
		return error(500, 'Failed to fetch template');
	}
};

export const PUT: RequestHandler = async ({ params, request, locals }) => {
	try {
		if (!locals.user) {
			return json({ error: 'Unauthorized' }, { status: 401 });
		}

		const templateId = params.id;
		const updateData = await request.json();

		// Ensure the user owns this template before updating
		const template = await db.template.findFirst({
			where: { id: templateId, userId: locals.user.id }
		});

		if (!template) {
			return error(404, 'Template not found or you do not have permission to edit it');
		}

		// Prepare the data for the update
		const dataToUpdate: UnknownRecord = { ...updateData };

		// If the status is being changed to 'published', also set 'is_public' to true
		if (updateData.status && updateData.status === 'published') {
			dataToUpdate.is_public = true;
		}

		const updatedTemplate = await db.template.update({
			where: { id: templateId },
			data: dataToUpdate
		});

		return json(updatedTemplate);
	} catch {
		return error(500, 'Failed to update template');
	}
};

export const DELETE: RequestHandler = async ({ params, locals }) => {
	try {
		if (!locals.user) {
			return json({ error: 'Unauthorized' }, { status: 401 });
		}

		const templateId = params.id;

		// Ensure the user owns this template before deleting
		const template = await db.template.findFirst({
			where: { id: templateId, userId: locals.user.id }
		});

		if (!template) {
			return error(404, 'Template not found or you do not have permission to delete it');
		}

		await db.template.delete({
			where: { id: templateId }
		});

		return json({ success: true, id: templateId });
	} catch {
		return error(500, 'Failed to delete template');
	}
};

import { json } from '@sveltejs/kit';
import type { RequestHandler } from './$types';
import { db } from '$lib/core/db';

export const POST: RequestHandler = async ({ request, locals }) => {
	try {
		console.log('Address  API called, user:', locals.user?.id);

		// Ensure user is authenticated
		if (!locals.user) {
			console.log('No user in locals');
			return json({ error: 'Unauthorized' }, { status: 401 });
		}

		const {
			_address,
			verified,
			representatives,
			street,
			city,
			state,
			zip,
			zipCode,
			countryCode,
			latitude,
			longitude,
			congressional_district
		} = await request.json();

		// Address  can be provided as a single string or as separate components
		let addressComponents = { street: '', city: '', state: '', zip: '' };

		if (street && city && state && (zipCode || zip)) {
			// Separate components provided
			addressComponents = { street, city, state, zip: zipCode || zip };
		} else if (_address) {
			// Parse full address string into components
			addressComponents = parseAddress(_address);
		} else {
			return json({ error: 'Address  information is required' }, { status: 400 });
		}

		// Update user with address components
		console.log('Updating user with address:', {
			street: addressComponents.street,
			city: addressComponents.city,
			state: addressComponents.state,
			zip: addressComponents.zip,
			congressional_district: congressional_district || undefined
		});

		const updatedUser = await db.user.update({
			where: { id: locals.user.id },
			data: {
				street: addressComponents.street,
				city: addressComponents.city,
				state: addressComponents.state,
				zip: addressComponents.zip,
				congressional_district: congressional_district || undefined,
				updatedAt: new Date()
			}
		});

		console.log('User updated successfully:', updatedUser.id);

		// If representatives were found, store them for this user
		if (representatives && representatives.length > 0) {
			// First, clear any existing representatives for this user
			await db.user_representatives.deleteMany({
				where: { user_id: locals.user.id }
			});

			// Store new representatives
			for (const rep of representatives) {
				// First, ensure the _representative exists in the database
				const existingRep = await db._representative.findFirst({
					where: {
						name: rep.name,
						state: rep.state,
						chamber: rep.chamber
					}
				});

				let _representativeId;

				if (existingRep) {
					_representativeId = existingRep.id;
				} else {
					// Create new _representative record
					const newRep = await db._representative.create({
						data: {
							bioguide_id: rep.bioguide_id || 'temp_' + Date.now(),
							name: rep.name,
							state: rep.state,
							district: rep.district,
							chamber: rep.chamber,
							party: rep.party || 'Unknown',
							email: rep.email || '',
							phone: rep.phone || '',
							office_code: rep.office_code || rep.office || ''
						}
					});
					_representativeId = newRep.id;
				}

				// Link _representative to user
				await db.user_representatives.create({
					data: {
						user_id: locals.user.id,
						_representative_id: _representativeId,
						relationship: 'constituent'
					}
				});
			}
		}

		return json({
			success: true,
			message: 'Address  saved successfully',
			user: {
				id: updatedUser.id,
				street: updatedUser.street,
				city: updatedUser.city,
				state: updatedUser.state,
				zip: updatedUser.zip,
				congressional_district: updatedUser.congressional_district
			}
		});
	} catch {
		console.error('Error occurred');
		return json(
			{
				error: 'Failed to save address',
				details: error instanceof Error ? error.message : 'Unknown error'
			},
			{ status: 500 }
		);
	}
};

function parseAddress(address: string): {
	street: string;
	city: string;
	state: string;
	zip: string;
} {
	// Basic address parsing - in production you'd want more robust parsing
	const parts = address.split(',').map((part) => part.trim());

	if (parts.length >= 3) {
		const street = parts[0];
		const city = parts[1];
		const stateZip = parts[2];

		// Extract state and ZIP from "STATE ZIP" format
		const stateZipMatch = stateZip.match(/^([A-Z]{2})\s+(\d{5}(-\d{4})?)$/);
		if (stateZipMatch) {
			return {
				street,
				city,
				state: stateZipMatch[1],
				zip: stateZipMatch[2]
			};
		}
	}

	// Fallback - return the address as street with empty other fields
	return {
		street: address,
		city: '',
		state: '',
		zip: ''
	};
}

/**
 * Add Verified Email Endpoint
 * Handles auto-verified email addition from bounce flow
 */

import { redirect } from '@sveltejs/kit';
import type { RequestHandler } from './$types';
import { verifySignedToken } from '$lib/core/auth/tokens';
import { db } from '$lib/core/db';
import { env } from '$env/dynamic/private';

export const GET: RequestHandler = async ({ url, locals }) => {
	const token = url.searchParams.get('token');
	const redirectPath = url.searchParams.get('redirect');

	if (!token) {
		throw redirect(302, '/settings/emails?error=missing_token');
	}

	try {
		// Verify the signed token
		const payload = await verifySignedToken<{
			email: string;
			user_id: string;
			templateSlug?: string;
			timestamp: number;
			purpose: string;
		}>(token, {
			secret: env.EMAIL_VERIFICATION_SECRET || env.JWT_SECRET || 'development-secret',
			maxAge: 24 * 60 * 60 // 24 hours
		});

		// Validate token purpose
		if (payload.purpose !== 'email_verification') {
			throw new Error('Invalid token purpose');
		}

		// Check if email already exists for this user
		const existingEmail = await db.userEmail.findFirst({
			where: {
				email: payload.email.toLowerCase(),
				userId: payload.user_id
			}
		});

		if (existingEmail) {
			// Email already added, just ensure it's verified
			if (!existingEmail.isVerified) {
				await db.userEmail.update({
					where: { id: existingEmail.id },
					data: {
						isVerified: true,
						verifiedAt: new Date()
					}
				});
			}
		} else {
			// Check if this email belongs to another user
			const emailInUse = await db.user.findUnique({
				where: { email: payload.email.toLowerCase() }
			});

			if (emailInUse) {
				throw redirect(302, '/settings/emails?error=email_in_use');
			}

			// Check secondary emails of other users
			const secondaryInUse = await db.userEmail.findFirst({
				where: {
					email: payload.email.toLowerCase(),
					NOT: { userId: payload.user_id }
				}
			});

			if (secondaryInUse) {
				throw redirect(302, '/settings/emails?error=email_in_use');
			}

			// Add and auto-verify the email
			await db.userEmail.create({
				data: {
					userId: payload.user_id,
					email: payload.email.toLowerCase(),
					isVerified: true,
					verifiedAt: new Date()
				}
			});
		}

		// Log the verification event
		await db.auditLog.create({
			data: {
				user_id: payload.user_id,
				action_type: 'email_added_via_bounce',
				audit_data: {
					email: payload.email,
					method: 'bounce_link',
					templateSlug: payload.templateSlug
				}
			}
		});

		// Redirect to the appropriate location
		const destination =
			redirectPath ||
			(payload.templateSlug
				? `/s/${payload.templateSlug}?email_added=true`
				: '/settings/emails?success=email_added');

		throw redirect(302, destination);
	} catch {
		console.error('Error occurred');

		// If it's already a redirect, pass it through
		if (error && typeof error === 'object' && 'status' in error && error.status === 302) {
			throw error;
		}

		// Otherwise redirect to error page
		throw redirect(302, '/settings/emails?error=invalid_token');
	}
};

import { json, error } from '@sveltejs/kit';
import type { RequestHandler } from './$types';
import { db } from '$lib/core/db';
import type { Representative as _Representative } from '$lib/types/user';

interface RepresentativeData {
	bioguide_id: string;
	name: string;
	party: string;
	state: string;
	district: string;
	chamber: 'house' | 'senate';
	office_code: string;
}

interface UserRepsData {
	house: RepresentativeData;
	senate: RepresentativeData[];
	district: {
		state: string;
		district: string;
	};
}

// POST /api/user/representatives - Store user's representatives after onboarding
export const POST: RequestHandler = async ({ request, locals }) => {
	try {
		// TODO: Add authentication check
		// const user = locals.user;
		// if (!user) {
		//     throw error(401, 'Authentication required');
		// }

		const data = await request.json();
		const { userId, representatives, userAddress } = data;

		// Validate required fields
		if (!userId) {
			throw error(400, 'userId is required');
		}

		if (!representatives || !representatives.house || !representatives.senate) {
			throw error(400, 'Representatives data is required (house and senate)');
		}

		// Validate representatives data structure
		const repsData: UserRepsData = representatives;

		// Start transaction to ensure data consistency
		const result = await db.$transaction(async (tx) => {
			// 1. Update user with address information if provided
			if (userAddress) {
				await tx.user.update({
					where: { id: userId },
					data: {
						street: userAddress.street,
						city: userAddress.city,
						state: userAddress.state,
						zip: userAddress.zip,
						congressional_district: `${repsData.district.state}-${repsData.district.district}`
					}
				});
			}

			// 2. Clear existing representatives for this user
			await tx.userrepresentatives.deleteMany({
				where: { user_id: userId }
			});

			// 3. Store/update representatives in representatives table
			const allReps = [repsData.house, ...repsData.senate];
			const storedReps = [];

			for (const rep of allReps) {
				const storedRep = await tx.representative.upsert({
					where: { bioguide_id: rep.bioguide_id },
					update: {
						name: rep.name,
						party: rep.party,
						state: rep.state,
						district: rep.district,
						chamber: rep.chamber,
						office_code: rep.office_code,
						is_active: true,
						last_updated: new Date()
					},
					create: {
						bioguide_id: rep.bioguide_id,
						name: rep.name,
						party: rep.party,
						state: rep.state,
						district: rep.district,
						chamber: rep.chamber,
						office_code: rep.office_code,
						is_active: true
					}
				});
				storedReps.push(storedRep);
			}

			// 4. Create user-representative relationships
			const userRepRelationships = [];

			// House representative
			const houseRep = storedReps.find((r) => r.chamber === 'house');
			if (houseRep) {
				userRepRelationships.push({
					user_id: userId,
					representative_id: houseRep.id,
					relationship: 'house',
					is_active: true
				});
			}

			// Senate representatives
			const senators = storedReps.filter((r) => r.chamber === 'senate');
			senators.forEach((senator, _index) => {
				userRepRelationships.push({
					user_id: userId,
					representative_id: senator.id,
					relationship: _index === 0 ? 'senate_senior' : 'senate_junior',
					is_active: true
				});
			});

			// Insert all relationships
			await tx.userrepresentatives.createMany({
				data: userRepRelationships
			});

			return {
				userId,
				representativesCount: storedReps.length,
				relationshipsCount: userRepRelationships.length,
				district: repsData.district
			};
		});

		return json({
			success: true,
			message: `Successfully stored ${result.representativesCount} representatives for user`,
			...result
		});
	} catch {
		// Re-throw SvelteKit errors
		if (error && typeof error === 'object' && 'status' in error) {
			throw error;
		}

		throw error(500, 'Failed to store user representatives');
	}
};

// GET /api/user/representatives - Get user's current representatives
export const GET: RequestHandler = async ({ url, locals }) => {
	try {
		// TODO: Add authentication check
		// const user = locals.user;
		// if (!user) {
		//     throw error(401, 'Authentication required');
		// }

		const userId = url.searchParams.get('userId');

		if (!userId) {
			throw error(400, 'userId parameter is required');
		}

		// Fetch user with their representatives
		const user = await db.user.findUnique({
			where: { id: userId },
			include: {
				representatives: {
					where: { is_active: true },
					include: {
						representative: true
					},
					orderBy: [
						{ relationship: 'asc' } // house, senate_junior, senate_senior
					]
				}
			}
		});

		if (!user) {
			throw error(404, 'User not found');
		}

		// Format response using unified Representative interface

		const representatives: {
			house: Representative | null;
			senate: Representative[];
		} = {
			house: null,
			senate: []
		};

		user.representatives.forEach((userRep) => {
			const rep: Representative = {
				id: userRep.representative.id,
				bioguide_id: userRep.representative.bioguide_id,
				name: userRep.representative.name,
				party: userRep.representative.party,
				state: userRep.representative.state,
				district: userRep.representative.district, // Keep as string
				chamber: userRep.representative.chamber,
				office_code: userRep.representative.office_code,
				is_active: userRep.representative.is_active ?? true,
				last_updated: userRep.representative.last_updated ?? new Date(),
				relationship: userRep.relationship,
				assigned_at: userRep.assigned_at,
				last_validated: userRep.last_validated
			};

			if (userRep.relationship === 'house') {
				representatives.house = rep;
			} else {
				representatives.senate.push(rep);
			}
		});

		return json({
			success: true,
