export const GET: RequestHandler = async ({ url }) => {
	return json({ message: 'VOTER Protocol API - Use POST for actions' });
};

export const POST: RequestHandler = async ({ request, url }) => {
	try {
		const { action, ...data } = await request.json();

		switch (action) {
			case 'submit_cwc_message':
				return await handleCWCSubmission(data);
			case 'record_civic_action':
				return await recordCivicAction(data);
			case 'update_reputation':
				return await updateReputation(data);
			case 'verify_identity':
				return await verifyIdentity(data);
			case 'get_user_profile':
				return await getUserProfile(data);
			case 'create_challenge':
				return await createChallenge(data);
			case 'process_challenge_vote':
				return await processChallengeVote(data);
			case 'calculate_reward':
				return await calculateReward(data);
			default:
				return json({ success: false, message: `Invalid action: ${action}` }, { status: 400 });
		}
	} catch {
		console.error('Error occurred');

		// Return specific err.message based on error type
		let statusCode = 500;
		let message = 'Internal server error';

		if (error instanceof Error) {
			message = err.message;

			// Handle specific SvelteKit HttpError status codes - check different properties
			if ('status' in error) {
				statusCode = (error as ErrorWithCode).status || 500;
			} else if ('code' in error && typeof (error as ErrorWithCode).code === 'number') {
				statusCode = (error as ErrorWithCode).code as number;
			} else if (error.name === 'HttpError') {
				// Try to extract status from HttpError
				const errorStr = error.toString();
				const statusMatch = errorStr.match(/status:\s*(\d+)/);
				if (statusMatch) {
					statusCode = parseInt(statusMatch[1]);
				}
			}
		}

		return json({ success: false, message }, { status: statusCode });
	}
};

/**
 * Handle CWC message submission using existing mature CWC client
 */
async function handleCWCSubmission({
	userId,
	templateId,
	message,
	representatives,
	metadata = {}
}: {
	userId: string;
	templateId: string;
	message: string;
	representatives: Representative[];
	metadata?: UnknownRecord;
}) {
	if (!userId || !templateId || !message) {
		throw error(400, 'Missing required fields: userId, templateId, message');
	}

	// Get user and template from database
	const user = await prisma.user.findUnique({
		where: { id: userId },
		select: {
			id: true,
			name: true,
			email: true,
			phone: true,
			street: true,
			city: true,
			state: true,
			zip: true,
			congressional_district: true
		}
	});

	if (!user) {
		return json({ success: false, message: 'User not found' }, { status: 404 });
	}

	const template = await prisma.template.findUnique({
		where: { id: templateId },
		select: {
			id: true,
			title: true,
			subject: true,
			message_body: true,
			delivery_config: true,
			cwc_config: true
		}
	});

	if (!template) {
		throw error(404, 'Template not found');
	}

	// Submit via existing mature CWC infrastructure
	const results = await cwcClient.submitToAllRepresentatives(
		template,
		user,
		representatives,
		message
	);

	// Initialize impact tracking with agent orchestration
	const coordinator = new AgentCoordinator();
	const impactAgent = new ImpactAgent();
	coordinator.registerAgent(impactAgent);

	// Prepare context for impact assessment
	const impactContext: AgentContext = {
		userId,
		actionType: 'cwc_message',
		templateId,
		timestamp: new Date().toISOString()
	};

	// Get impact agent decision for this CWC submission
	let impactDecision = null;
	try {
		impactDecision = await impactAgent.makeDecision(impactContext);
	} catch (errorimpactError) {
		console.warn('Impact agent decision failed:', impactError);
	}

	// Record civic action with new blockchain-focused model
	const civicAction = await prisma.civicAction.create({
		data: {
			user_id: userId,
			template_id: templateId,
			action_type: 'cwc_message',
			status: results.every((r) => r.success) ? 'confirmed' : 'failed',
			consensus_data: {
				impact_assessment: impactDecision
					? {
							agentId: impactDecision.agentId,
							confidence: impactDecision.confidence,
							reasoning: impactDecision.reasoning,
							impactScore: ((impactDecision.decision as UnknownRecord)?.impactScore as number) || 0
						}
					: { error: 'Impact assessment failed' }
			},
			confirmed_at: results.every((r) => r.success) ? new Date() : null
		}
	});

	// Create unified audit log entry
	const auditLog = await prisma.auditLog.create({
		data: {
			user_id: userId,
			action_type: 'civic_action',
			action_subtype: 'cwc_message',
			audit_data: JSON.parse(
				JSON.stringify({
					cwc_results: results,
					submitted_at: new Date().toISOString(),
					message_preview: message.substring(0, 100) + '...',
					representatives_count: representatives.length,
					delivery_method: 'cwc',
					template_id: templateId
				})
			),
			agent_source: impactDecision?.agentId || null,
			agent_decisions: impactDecision
				? JSON.parse(
						JSON.stringify({
							impact: {
								agentId: impactDecision.agentId,
								confidence: impactDecision.confidence,
								reasoning: impactDecision.reasoning,
								decision: impactDecision.decision
							}
						})
					)
				: undefined,
			confidence: impactDecision?.confidence || null,
			civic_action_id: civicAction.id,
			status: results.every((r) => r.success) ? 'completed' : 'failed',
			metadata: {
				...metadata,
				impact_tracking_enabled: true
			}
		}
	});

	return json({
		success: true,
		actionId: civicAction.id,
		auditLogId: auditLog.id,
		results,
		impact_assessment: impactDecision
			? {
					impactScore: ((impactDecision.decision as UnknownRecord)?.impactScore as number) || 0,
					confidence: impactDecision.confidence,
					reasoning: impactDecision.reasoning
				}
			: null,
		message: `Successfully submitted to ${results.filter((r) => r.success).length}/${results.length} representatives`
	});
}

/**
 * Record civic action with blockchain integration
 */
async function recordCivicAction({
	userId,
	actionType,
	templateId,
	txHash,
	rewardWei,
	agentDecisions = {},
	metadata = {},
	status = 'completed'
}: {
	userId: string;
	actionType: string;
	templateId?: string;
	txHash?: string;
	rewardWei?: string;
	agentDecisions?: UnknownRecord;
	metadata?: UnknownRecord;
	status?: string;
}) {
	if (!userId || !actionType) {
		return json(
			{ success: false, message: 'Missing required fields: userId, actionType' },
			{ status: 400 }
		);
	}

	// Create blockchain-focused civic action
	const action = await prisma.civicAction.create({
		data: {
			user_id: userId,
			template_id: templateId,
			action_type: actionType,
			tx_hash: txHash,
			reward_wei: rewardWei,
			status: status === 'completed' ? 'confirmed' : status,
			consensus_data: agentDecisions,
			confirmed_at: status === 'completed' ? new Date() : null
		}
	});

	// Create unified audit log entry
	const auditLog = await prisma.auditLog.create({
		data: {
			user_id: userId,
			action_type: 'civic_action',
			action_subtype: actionType,
			audit_data: {
				template_id: templateId,
				tx_hash: txHash,
				reward_wei: rewardWei,
				...metadata
			},
			agent_decisions: agentDecisions,
			civic_action_id: action.id,
			status: status === 'completed' ? 'completed' : status
		}
	});

	return json({
		success: true,
		actionId: action.id,
		auditLogId: auditLog.id,
		message: `Civic action ${actionType} recorded`
	});
}

/**
 * Agent-orchestrated reputation assessment with ERC-8004 compliance
 * Replaces static reputation updates with comprehensive credibility analysis
 */
async function updateReputation({
	userId,
	scoreChange,
	reason,
	txHash,
	agentSource,
	evidence,
	confidence
}: {
	userId: string;
	scoreChange?: number;
	reason?: string;
	txHash?: string;
	agentSource?: string;
	evidence?: UnknownRecord;
	confidence?: number;
}) {
	if (!userId) {
		throw error(400, 'Missing required field: userId');
	}

	// Initialize ReputationAgent for comprehensive assessment
	const reputationAgent = new ReputationAgent();

	const reputationContext: AgentContext = {
		userId,
		actionType: 'reputation_update',
		timestamp: new Date().toISOString()
	};

	// Get agent-based credibility assessment
	let reputationDecision = null;
	try {
		reputationDecision = await reputationAgent.makeDecision(reputationContext);
	} catch (errorreputationError) {
		console.error('Reputation agent failed:', reputationError);

		// Fallback to legacy reputation update
		const user = await prisma.user.findUnique({
			where: { id: userId },
			select: { trust_score: true, reputation_tier: true }
		});

		if (!user) {
			throw error(404, 'User not found');
		}

		const oldScore = user.trust_score;
		const newScore = Math.max(0, oldScore + (scoreChange || 0));

		return json({
			success: true,
			oldScore,
			newScore,
			newTier: user.reputation_tier,
			warning: 'Used fallback reputation calculation',
			message: `Reputation updated: ${oldScore} â†’ ${newScore} (${reason || 'fallback'})`
		});
	}

	// Use type-safe extraction for reputation decision
	const credibilityAssessment = extractReputationDecision(reputationDecision.decision);

	// Get current user state for comparison
	const user = await prisma.user.findUnique({
		where: { id: userId },
		select: { trust_score: true, reputation_tier: true }
	});

	if (!user) {
		throw error(404, 'User not found');
	}

	const oldScore = user.trust_score;
	const newScore = credibilityAssessment.credibilityScore;

	// Update user with agent-determined credibility score and tier
	await prisma.user.update({
		where: { id: userId },
		data: {
			trust_score: newScore,
			reputation_tier: credibilityAssessment.tier
		}
	});

	// Log comprehensive reputation assessment with unified audit system
	await prisma.auditLog.create({
		data: {
			user_id: userId,
			action_type: 'reputation_change',
			action_subtype: 'agent_credibility_assessment',
			audit_data: JSON.parse(
				JSON.stringify({
					components: credibilityAssessment.credibilityComponents,
					badges: credibilityAssessment.badges,
					riskFactors: credibilityAssessment.riskFactors,
					attestations: credibilityAssessment.attestations,
					tx_hash: txHash,
					portabilityHash: credibilityAssessment.portabilityHash
				})
			),
			score_before: oldScore,
			score_after: newScore,
			change_amount: newScore - oldScore,
			change_reason: reason || 'agent_credibility_assessment',
			agent_source: reputationDecision.agentId,
			evidence_hash: JSON.stringify({
				components: credibilityAssessment.credibilityComponents,
				badges: credibilityAssessment.badges,
				riskFactors: credibilityAssessment.riskFactors,
				attestations: credibilityAssessment.attestations.length
			}),
			confidence: reputationDecision.confidence,
			status: 'completed'
		}
	});

	return json({
		success: true,
		oldScore,
		newScore,
		newTier: credibilityAssessment.tier,
		credibility_assessment: {
			components: credibilityAssessment.credibilityComponents,
			badges: credibilityAssessment.badges,
			riskFactors: credibilityAssessment.riskFactors,
			attestations: credibilityAssessment.attestations.length,
			portabilityHash: credibilityAssessment.portabilityHash
		},
		agent_reasoning: {
			confidence: reputationDecision.confidence,
			reasoning: reputationDecision.reasoning
		},
		message: `ERC-8004 credibility assessment: ${oldScore} â†’ ${newScore} (${credibilityAssessment.tier} tier)`
	});
}

/**
 * Agent-orchestrated identity verification
 * Replaces static verification with intelligent multi-source analysis
 */
async function verifyIdentity({
	userId,
	walletAddress,
	kycResult,
	trustScore,
	districtHash
}: {
	userId: string;
	walletAddress: string;
	kycResult: KYCResult;
	trustScore: number;
	districtHash: string;
}) {
	if (!userId) {
		throw error(400, 'Missing required field: userId');
	}

	// First update user data from external verification
	const updatedUser = await prisma.user.update({
		where: { id: userId },
		data: {
			wallet_address: walletAddress,
			verification_method: 'didit',
			verification_data: kycResult,
			verified_at: new Date(),
			district_hash: districtHash,
			congressional_district: kycResult?.addressData?.congressionalDistrict
		}
	});

	// Initialize verification agent for comprehensive assessment
	const verificationAgent = new VerificationAgent();

	const verificationContext: AgentContext = {
		userId,
		actionType: 'identity_verification'
	};

	// Get agent-based verification assessment
	let verificationDecision = null;
	try {
		verificationDecision = await verificationAgent.makeDecision(verificationContext);
	} catch (errorverificationError) {
		console.error('Verification agent failed:', verificationError);
		// Fallback to basic trust score logic
		verificationDecision = {
			decision: {
				verificationLevel: trustScore >= 60 ? 'verified' : 'unverified',
				trustScore: trustScore || 0,
				riskFactors: [],
				recommendedActions: []
			},
			confidence: 0.5,
			reasoning: 'Fallback verification due to agent error'
		};
	}

	// Use type-safe extraction for verification decision
	const assessment = extractVerificationDecision(verificationDecision.decision);

	// Update user with agent-determined trust score and verification status
	await prisma.user.update({
		where: { id: userId },
		data: {
			trust_score: assessment.trustScore,
			is_verified: ['verified', 'high_assurance'].includes(assessment.verificationLevel),
			reputation_tier:
				assessment.trustScore >= 500
					? 'expert'
					: assessment.trustScore >= 100
						? 'verified'
						: 'novice'
		}
	});

	// Log verification change with agent reasoning using unified audit system
	await prisma.auditLog.create({
		data: {
			user_id: userId,
			action_type: 'verification',
			action_subtype: 'identity_verification',
			audit_data: {
				verificationLevel: assessment.verificationLevel,
				verificationSources: assessment.verificationSources,
				riskFactors: assessment.riskFactors,
				recommendations: assessment.recommendedActions,
				walletAddress,
				kycResult,
				districtHash
			} as unknown as Prisma.JsonObject,
			score_before: 0,
			score_after: assessment.trustScore,
			change_amount: assessment.trustScore,
			change_reason: 'agent_identity_verification',
			certification_type: 'identity_verification',
			certification_data: {
				verificationLevel: assessment.verificationLevel,
				sources: assessment.verificationSources?.length || 0
			},
			agent_source: verificationDecision.agentId,
			confidence: verificationDecision.confidence,
			evidence_hash: JSON.stringify({
				verificationSources: assessment.verificationSources?.length || 0,
				riskFactors: assessment.riskFactors,
				recommendations: assessment.recommendedActions
			}),
			status: 'completed'
		}
	});

	return json({
		success: true,
		userId,
		trustScore: assessment.trustScore,
		verificationLevel: assessment.verificationLevel,
		congressionalDistrict: kycResult?.addressData?.congressionalDistrict,
		agent_assessment: {
			confidence: verificationDecision.confidence,
			reasoning: verificationDecision.reasoning,
			riskFactors: assessment.riskFactors,
			recommendations: assessment.recommendedActions,
			sourcesAnalyzed: assessment.verificationSources?.length || 0
		},
		message: `Identity verification completed: ${assessment.verificationLevel} level`
	});
}

/**
 * Get user profile with civic action history
 */
async function getUserProfile({
	userId,
	walletAddress
}: {
	userId?: string;
	walletAddress?: string;
}) {
	if (!userId && !walletAddress) {
		throw error(400, 'Must provide either userId or walletAddress ');
	}

	const user = (await prisma.user.findFirst({
		where: userId ? { id: userId } : { wallet_address: walletAddress },
		include: {
			civic_actions: {
				orderBy: { created_at: 'desc' },
				take: 10,
				include: {
					template: {
						select: { title: true, category: true }
					}
				}
			},
			audit_logs: {
				where: {
					action_type: 'reputation_change'
				},
				orderBy: { created_at: 'desc' },
				take: 5
			},
			_count: {
				select: {
					civic_actions: true,
					challenger_challenges: true,
					defender_challenges: true,
					won_challenges: true
				}
			}
		}
	})) as UserProfileData;

	if (!user) {
		throw error(404, 'User not found');
	}

	return json({
		success: true,
		user: {
			id: user.id,
			name: user.name,
			email: user.email,
			wallet_address: user.wallet_address,
			trust_score: user.trust_score,
			reputation_tier: user.reputation_tier,
			is_verified: user.is_verified,
			verification_method: user.verification_method,
			congressional_district: user.congressional_district,
			created_at: user.createdAt
		},
		stats: {
			civic_actions_count: user._count.civic_actions,
			challenges_created: user._count.challenger_challenges,
			challenges_defended: user._count.defender_challenges,
			challenges_won: user._count.won_challenges
		},
		recent_actions: user.civic_actions.map((action: UnknownRecord) => ({
			id: action.id,
			type: action.action_type,
			template_title: action.template?.title,
			status: action.status,
			reward_wei: action.reward_wei,
			created_at: action.created_at
		})),
		reputation_history: user.audit_logs.map((log: UnknownRecord) => ({
			score_change: log.change_amount,
			reason: log.change_reason,
			created_at: log.created_at
		}))
	});
}

/**
 * Create challenge in challenge market
 */
async function createChallenge({
	challengerId,
	defenderId,
	title,
	description,
	evidenceIPFS,
	stakeAmount,
	category
}: {
	challengerId: string;
	defenderId: string;
	title: string;
	description: string;
	evidenceIPFS: string;
	stakeAmount: string;
	category: string;
}) {
	if (!challengerId || !defenderId || !title || !evidenceIPFS || !stakeAmount) {
		throw error(400, 'Missing required fields for challenge creation');
	}

	// Calculate voting deadline (72 hours from now)
	const votingDeadline = new Date();
	votingDeadline.setHours(votingDeadline.getHours() + 72);

	// Generate unique claim hash
	const claimHash = `0x${Date.now().toString(16)}${Math.random().toString(16).substring(2)}`;

	const challenge = await prisma.challenge.create({
		data: {
			challenger_id: challengerId,
			defender_id: defenderId,
			title,
			description,
			evidence_ipfs: evidenceIPFS,
			stake_amount: stakeAmount,
			voting_deadline: votingDeadline,
			claim_hash: claimHash,
			category,
			status: 'active'
		}
	});

	return json({
		success: true,
		challengeId: challenge.id,
		claimHash,
		votingDeadline,
		message: 'Challenge created successfully'
	});
}

/**
 * Process challenge vote with quadratic voting
 */
async function processChallengeVote({
	challengeId,
	userId,
	side,
	stakeAmount
}: ProcessChallengeVoteParams) {
	if (!challengeId || !userId || !side || !stakeAmount) {
		throw error(400, 'Missing required fields for challenge vote');
	}

	// Calculate quadratic voting power
	const votingPower = Math.sqrt(parseFloat(stakeAmount));

	const stake = await prisma.challengeStake.upsert({
		where: {
			challenge_iduser_id: {
				challenge_id: challengeId,
				user_id: userId
			}
		},
		create: {
			challenge_id: challengeId,
			user_id: userId,
			amount: stakeAmount,
			side,
			voting_power: votingPower
		},
		update: {
			amount: stakeAmount,
			side,
			voting_power: votingPower
		}
	});

	return json({
		success: true,
		stakeId: stake.id,
		votingPower,
		message: `Vote recorded: ${side} with ${votingPower.toFixed(2)} voting power`
	});
}

/**
 * Agent-orchestrated reward calculation
 * Replaces static logic with intelligent agents
 */
async function calculateReward({
	userAddress,
	actionType,
	templateId,
	timestamp
}: RewardCalculationRequest) {
	if (!userAddress || !actionType) {
		throw error(400, 'Missing required fields: userAddress , actionType');
	}

	// Get user for context
	const user = await prisma.user.findUnique({
		where: { wallet_address: userAddress },
		select: { id: true, trust_score: true, reputation_tier: true }
	});

	// Initialize agent coordinator
	const coordinator = new AgentCoordinator();
	const supplyAgent = new SupplyAgent();
	const impactAgent = new ImpactAgent();

	coordinator.registerAgent(supplyAgent);
	coordinator.registerAgent(impactAgent);

	// Build agent context
	const context: AgentContext = {
		userId: user?.id,
		actionType,
		templateId,
		timestamp
	};

	// Get agent decisions for reward calculation
	const consensus = await coordinator.coordinateDecision(context, [
		AgentType.SUPPLY,
		AgentType.IMPACT
	]);

	if (!consensus.consensusReached) {
		console.warn('Agent consensus failed, using fallback calculation');

		// Fallback to simple calculation
		const fallbackRewardWei = '100000000000000000'; // 0.1 ETH

		return json({
			success: true,
			action: actionType,
			user: userAddress,
			reward_wei: fallbackRewardWei,
			warning: 'Used fallback calculation due to agent consensus failure',
			agent_decisions: consensus.decisions,
			calculation_timestamp: new Date().toISOString()
		});
	}

	// Extract supply agent decision (contains reward parameters)
	const supplyDecision = consensus.decisions.find((d) => d.agentType === AgentType.SUPPLY);
	const impactDecision = consensus.decisions.find((d) => d.agentType === AgentType.IMPACT);

	if (!supplyDecision) {
		throw error(500, 'Supply agent decision missing from consensus');
	}

	const rewardParams = supplyDecision.decision as RewardParameters;

	// Save agent decisions for audit trail
	await prisma.rewardCalculation.create({
		data: {
			user_address: userAddress,
			action_type: actionType,
			base_reward_usd: rewardParams.baseRewardUSD?.toString() || '0.10',
			total_multiplier: rewardParams.totalMultiplier?.toString() || '1.0',
			reward_usd: (
				(rewardParams.baseRewardUSD || 0.1) * (rewardParams.totalMultiplier || 1.0)
			).toString(),
			reward_wei: rewardParams.finalRewardWei || '100000000000000000',
			eth_price: rewardParams.ethPrice?.toString() || '2000',
			multipliers: rewardParams.multipliers || {},
			network_activity: {
				daily_active_users: supplyDecision.parameters?.networkActivity || 0
			},
			agent_decisions: {
				supply: {
					agentId: supplyDecision.agentId,
					confidence: supplyDecision.confidence,
					reasoning: supplyDecision.reasoning
				},
				impact: impactDecision
					? {
							agentId: impactDecision.agentId,
							confidence: impactDecision.confidence,
							reasoning: impactDecision.reasoning,
							impactScore: impactDecision.decision?.impactScore || 0
						}
					: null
			}
		}
	});

	return json({
		success: true,
		action: actionType,
		user: userAddress,
		base_reward_usd: rewardParams.baseRewardUSD || 0.1,
		multipliers: rewardParams.multipliers || {},
		total_multiplier: rewardParams.totalMultiplier || 1.0,
		reward_usd: (rewardParams.baseRewardUSD || 0.1) * (rewardParams.totalMultiplier || 1.0),
		reward_eth: rewardParams.finalRewardETH || 0.05,
		reward_wei: rewardParams.finalRewardWei || '100000000000000000',
		eth_price: rewardParams.ethPrice || 2000,
		network_activity: supplyDecision.parameters?.networkActivity || 0,
		user_reputation: user?.trust_score || 0,
		confidence: consensus.consensusConfidence,
		agent_reasoning: {
			supply: supplyDecision.reasoning,
			impact: impactDecision?.reasoning
		},
		impact_score: impactDecision?.decision?.impactScore || 0,
		calculation_timestamp: new Date().toISOString()
	});
}

/** VOTER Protocol Challenge Market API
 *
 * Handles challenge creation, voting, and resolution
 * Called by N8N challenge market workflow
 */

import { json, error } from '@sveltejs/kit';
import { prisma } from '$lib/core/db.js';
import type { RequestHandler } from './$types';
import type { Prisma } from '@prisma/client';
import type { DatabaseWhereClause, UnknownRecord } from '$lib/types/any-replacements.js';

export const POST: RequestHandler = async ({ request }) => {
	try {
		const requestData: unknown = await request.json();

		// Type guard for request data
		const isValidRequestData = (
			obj: unknown
		): obj is {
			action: string;
			[key: string]: unknown;
		} => {
			return (
				typeof obj === 'object' &&
				obj !== null &&
				'action' in obj &&
				typeof (obj as UnknownRecord).action === 'string'
			);
		};

		if (!isValidRequestData(requestData)) {
			throw error(400, 'Invalid request data format');
		}

		const { action, ...data } = requestData;

		switch (action) {
			case 'create':
				return await createChallenge(data);
			case 'vote':
				return await voteOnChallenge(data);
			case 'resolve':
				return await resolveChallenge(data);
			case 'claim':
				return await claimRewards(data);
			default:
				throw error(400, `Invalid challenge action: ${action}`);
		}
	} catch {
		console.error('Error occurred');
		throw error(500, error ? 'Unknown error' : 'Challenge operation failed');
	}
};

export const GET: RequestHandler = async ({ url }) => {
	try {
		const challengeId = url.searchParams.get('id');
		const status = url.searchParams.get('status');
		const userId = url.searchParams.get('userId');

		if (challengeId) {
			// Get specific challenge with vote tallies
			const challenge = await prisma.challenge.findUnique({
				where: { id: challengeId },
				include: {
					challenger: {
						select: { id: true, name: true, reputation_tier: true }
					},
					defender: {
						select: { id: true, name: true, reputation_tier: true }
					},
					winner: {
						select: { id: true, name: true }
					},
					stakes: {
						include: {
							user: {
								select: { id: true, name: true }
							}
						}
					}
				}
			});

			if (!challenge) {
				throw error(404, 'Challenge not found');
			}

			// Calculate vote tallies
			const supportPower = challenge.stakes
				.filter((s) => s.side === 'support')
				.reduce((sum, s) => sum + s.voting_power, 0);

			const opposePower = challenge.stakes
				.filter((s) => s.side === 'oppose')
				.reduce((sum, s) => sum + s.voting_power, 0);

			return json({
				success: true,
				challenge: {
					...challenge,
					vote_tally: {
						support_power: supportPower,
						oppose_power: opposePower,
						total_power: supportPower + opposePower,
						total_voters: challenge.stakes.length
					}
				}
			});
		}

		// List challenges with filters
		const where: DatabaseWhereClause = {};
		if (status) {
			where.status = status;
		}
		if (userId) {
			where.OR = [
				{ challenger_id: userId },
				{ defender_id: userId },
				{ stakes: { some: { user_id: userId } } }
			];
		}

		const challenges = await prisma.challenge.findMany({
			where,
			include: {
				challenger: { select: { name: true } },
				defender: { select: { name: true } },
				_count: { select: { stakes: true } }
			},
			orderBy: { created_at: 'desc' },
			take: 50
		});

		return json({
			success: true,
			challenges: challenges.map((c) => ({
				id: c.id,
				title: c.title,
				status: c.status,
				challenger: c.challenger.name,
				defender: c.defender.name,
				stake_amount: c.stake_amount,
				voting_deadline: c.voting_deadline,
				votes_count: c._count.stakes,
				created_at: c.created_at
			}))
		});
	} catch {
		console.error('Error occurred');
		throw error(500, error ? 'Unknown error' : 'Failed to get challenges');
	}
};

/**
 * Create new challenge
 */
async function createChallenge(data: Record<string, unknown>) {
	const { challengerId, defenderId, title, description, evidence, stakeAmount, category } = data;
	if (!challengerId || !defenderId || !title || !evidence || !stakeAmount) {
		throw error(400, 'Missing required fields for challenge creation');
	}

	// Type assertions after validation
	const challengerIdStr = challengerId as string;
	const defenderIdStr = defenderId as string;
	const titleStr = title as string;
	const evidenceStr = evidence as string;
	const stakeAmountStr = stakeAmount as string;

	// Validate stake amount (10-10,000 VOTER tokens)
	const stake = parseFloat(stakeAmountStr);
	if (stake < 10 || stake > 10000) {
		throw error(400, 'Stake amount must be between 10 and 10,000 VOTER tokens');
	}

	// Verify users exist
	const [challenger, defender] = await Promise.all([
		prisma.user.findUnique({ where: { id: challengerIdStr } }),
		prisma.user.findUnique({ where: { id: defenderIdStr } })
	]);

	if (!challenger || !defender) {
		throw error(404, 'Challenger or defender not found');
	}

	// Check challenger has sufficient trust score to create challenges
	if (challenger.trust_score < 60) {
		throw error(403, 'Insufficient trust score to create challenges (minimum 60 required)');
	}

	// Generate challenge ID and claim hash
	const challengeId = `ch_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
	const claimHash = `0x${Buffer.from(challengeId).toString('hex')}`;

	// Set voting deadline (72 hours from now)
	const votingDeadline = new Date();
	votingDeadline.setHours(votingDeadline.getHours() + 72);

	// Create challenge in database via main API
	const response = await fetch(`${process.env.ORIGIN}/api/voter`, {
		method: 'POST',
		headers: { 'Content-Type': 'application/json' },
		body: JSON.stringify({
			action: 'create_challenge',
			challengerId: challengerIdStr,
			defenderId: defenderIdStr,
			title: titleStr,
			description,
			evidenceIPFS: evidenceStr,
			stakeAmount: stakeAmountStr,
			category
		})
	});

	if (!response.ok) {
		throw error(500, 'Failed to create challenge');
	}

	const result = await response.json();

	// Log challenge creation in audit system
	await prisma.auditLog.create({
		data: {
			user_id: challengerIdStr,
			action_type: 'civic_action',
			action_subtype: 'challenge_create',
			audit_data: {
				challenge_id: result.challengeId,
				defender_id: defenderIdStr,
				title: titleStr,
				description: description as string,
				evidence_ipfs: evidenceStr,
				stake_amount: stakeAmountStr,
				category: category as string,
				voting_deadline: result.votingDeadline
			},
			status: 'completed'
		}
	});

	return json({
		success: true,
		challengeId: result.challengeId,
		claimHash: result.claimHash,
		stake: stake,
		influence: Math.sqrt(stake), // Quadratic scaling
		status: 'active',
		votingDeadline: result.votingDeadline,
		message: 'Challenge created successfully'
	});
}

/**
 * Vote on challenge with quadratic voting
 */
async function voteOnChallenge(data: Record<string, unknown>) {
	const { challengeId, userId, vote, stakeAmount } = data;
	if (!challengeId || !userId || !vote || !stakeAmount) {
		throw error(400, 'Missing required fields for challenge vote');
	}

	// Type assertions after validation
	const challengeIdStr = challengeId as string;
	const userIdStr = userId as string;
	const voteStr = vote as string;
	const stakeAmountStr = stakeAmount as string;

	// Validate vote
	if (!['support', 'oppose'].includes(voteStr)) {
		throw error(400, 'Vote must be either "support" or "oppose"');
	}

	// Validate stake amount
	const stake = parseFloat(stakeAmountStr);
	if (stake < 1) {
		throw error(400, 'Minimum stake is 1 VOTER token');
	}

	// Check challenge exists and is still active
	const challenge = await prisma.challenge.findUnique({
		where: { id: challengeIdStr },
		select: {
			status: true,
			voting_deadline: true,
			challenger_id: true,
			defender_id: true
		}
	});

	if (!challenge) {
		throw error(404, 'Challenge not found');
	}

	if (challenge.status !== 'active') {
		throw error(400, 'Challenge is no longer active');
	}

	if (new Date() > challenge.voting_deadline) {
		throw error(400, 'Voting deadline has passed');
	}

	// Prevent self-voting
	if (userIdStr === challenge.challenger_id || userIdStr === challenge.defender_id) {
		throw error(403, 'Challenge participants cannot vote on their own challenge');
	}

	// Record vote via main API
	const response = await fetch(`${process.env.ORIGIN}/api/voter`, {
		method: 'POST',
		headers: { 'Content-Type': 'application/json' },
		body: JSON.stringify({
			action: 'process_challenge_vote',
			challengeId: challengeIdStr,
			userId: userIdStr,
			side: voteStr,
			stakeAmount: stakeAmountStr
		})
	});

	if (!response.ok) {
		throw error(500, 'Failed to record vote');
	}

	const result = await response.json();

	// Log challenge vote in audit system
	await prisma.auditLog.create({
		data: {
			user_id: userIdStr,
			action_type: 'civic_action',
			action_subtype: 'challenge_vote',
			audit_data: {
				challenge_id: challengeIdStr,
				vote: voteStr,
				stake_amount: stakeAmountStr,
				voting_power: result.votingPower
			},
			status: 'completed'
		}
	});

	return json({
		success: true,
		challengeId: challengeIdStr,
		voter: userIdStr,
		vote: voteStr,
		stake: stake,
		votingPower: result.votingPower,
		timestamp: new Date().toISOString(),
		message: `Vote recorded: ${voteStr} with ${result.votingPower.toFixed(2)} voting power`
	});
}

/**
 * Resolve challenge based on votes
 */
async function resolveChallenge(data: Record<string, unknown>) {
	const { challengeId } = data;
	if (!challengeId) {
		throw error(400, 'Missing challengeId');
	}

	// Type assertion after validation
	const challengeIdStr = challengeId as string;

	const challenge = await prisma.challenge.findUnique({
		where: { id: challengeIdStr },
		include: {
			stakes: true,
			challenger: { select: { name: true } },
			defender: { select: { name: true } }
		}
	});

	if (!challenge) {
		throw error(404, 'Challenge not found');
	}

	// Check if voting period has ended
	const votingEnded = new Date() > challenge.voting_deadline;
	if (!votingEnded) {
		throw error(400, 'Voting period has not ended');
	}

	if (challenge.status !== 'active') {
		throw error(400, 'Challenge is not in active status');
	}

	// Calculate vote tallies
	const supportPower = challenge.stakes
		.filter((s) => s.side === 'support')
		.reduce((sum, s) => sum + s.voting_power, 0);

	const opposePower = challenge.stakes
		.filter((s) => s.side === 'oppose')
		.reduce((sum, s) => sum + s.voting_power, 0);

	const totalVoters = challenge.stakes.length;
	const totalPower = supportPower + opposePower;

	// Require minimum participation (5 voters)
	if (totalVoters < 5) {
		await prisma.challenge.update({
			where: { id: challengeIdStr },
			data: {
				status: 'cancelled',
				resolved_at: new Date(),
				resolution: 'insufficient_participation'
			}
		});

		return json({
			success: true,
			status: 'cancelled',
			reason: 'insufficient_participation',
			supportPower,
			opposePower,
			totalVoters,
			message: 'Challenge cancelled due to insufficient participation'
		});
	}

	// Determine outcome (60% threshold for challenge to succeed)
	const challengeSucceeds = supportPower > opposePower * 1.5;
	const resolution = challengeSucceeds ? 'challenger_wins' : 'defender_wins';
	const winnerId = challengeSucceeds ? challenge.challenger_id : challenge.defender_id;

	// Update challenge status
	await prisma.challenge.update({
		where: { id: challengeIdStr },
		data: {
			status: 'resolved',
			resolution,
			winner_id: winnerId,
			resolved_at: new Date()
		}
	});

	// Calculate reward distribution
	const challengerStake = parseFloat(challenge.stake_amount);
	let rewards: Record<string, number> = {};

	if (challengeSucceeds) {
		// Challenge succeeded - distribute stakes to supporters
		rewards = {
			challenger_reward: challengerStake * 0.3,
			supporter_pool: challengerStake * 0.6,
			treasury: challengerStake * 0.1
		};
	} else {
		// Challenge failed - distribute challenger stake to opposers
		rewards = {
			defender_reward: challengerStake * 0.3,
			opposer_pool: challengerStake * 0.6,
			treasury: challengerStake * 0.1
		};
	}

	// Log challenge resolution in audit system
	await prisma.auditLog.create({
		data: {
			user_id: winnerId,
			action_type: 'civic_action',
			action_subtype: 'challenge_resolve',
			audit_data: {
				challenge_id: challengeIdStr,
				resolution,
				support_power: supportPower,
				oppose_power: opposePower,
				total_voters: totalVoters,
				total_power: totalPower,
				rewards
			},
			status: 'completed'
		}
	});

	return json({
		success: true,
		challengeId,
		status: 'resolved',
		resolution,
		winner: challengeSucceeds ? challenge.challenger.name : challenge.defender.name,
		supportPower,
		opposePower,
		totalPower,
		totalVoters,
		unanimous: supportPower === 0 || opposePower === 0,
		rewards,
		resolved_at: new Date().toISOString(),
		message: `Challenge resolved: ${resolution}`
	});
}

/**
 * Claim rewards for challenge participants
 */
async function claimRewards(data: Record<string, unknown>) {
	const { challengeId, userId } = data;
	if (!challengeId || !userId) {
		throw error(400, 'Missing challengeId or userId');
	}

	// Type assertions after validation
	const challengeIdStr = challengeId as string;
	const userIdStr = userId as string;

	const challenge = await prisma.challenge.findUnique({
		where: { id: challengeIdStr },
		include: {
			stakes: {
				where: { user_id: userIdStr },
				include: { user: { select: { name: true } } }
			}
		}
	});

	if (!challenge) {
		throw error(404, 'Challenge not found');
	}

	if (challenge.status !== 'resolved') {
		throw error(400, 'Challenge is not resolved yet');
	}

	const userStake = challenge.stakes[0];
	if (!userStake) {
		throw error(404, 'User did not participate in this challenge');
	}

	if (userStake.claimed) {
		throw error(400, 'Rewards already claimed');
	}

	// Check if user was on winning side
	const wasWinner =
		(challenge.resolution === 'challenger_wins' && userStake.side === 'support') ||
		(challenge.resolution === 'defender_wins' && userStake.side === 'oppose');

	if (!wasWinner) {
		throw error(400, 'User was not on winning side');
	}

	// Calculate individual reward (proportional to voting power)
	// This is a simplified calculation - in production would need more complex logic
	const totalWinningPower = challenge.stakes
		.filter(
			(s) =>
				(challenge.resolution === 'challenger_wins' && s.side === 'support') ||
				(challenge.resolution === 'defender_wins' && s.side === 'oppose')
		)
		.reduce((sum, s) => sum + s.voting_power, 0);

	const challengerStake = parseFloat(challenge.stake_amount);
	const rewardPool = challengerStake * 0.6; // 60% goes to winning voters
	const individualReward = (userStake.voting_power / totalWinningPower) * rewardPool;

	// Mark stake as claimed
	await prisma.challengeStake.update({
		where: { id: userStake.id },
		data: {
			claimed: true,
			claimed_at: new Date()
		}
	});

	return json({
		success: true,
		challengeId,
		userId,
		reward: individualReward,
		reward_wei: Math.floor(individualReward * 1e18).toString(),
		claimed_at: new Date().toISOString(),
		message: `Reward claimed: ${individualReward.toFixed(4)} VOTER tokens`
	});
}

/** VOTER Protocol Identity Verification API
 *
 * Handles Didit KYC integration and trust score calculation
 * Called by N8N identity verification workflow
 */

import { json, error } from '@sveltejs/kit';
