		author: template.user
			? {
					name: template.user.name,
					avatar: template.user.avatar
				}
			: null,
		createdAt: template.createdAt.toISOString()
	};

	return {
		template: formattedTemplate,
		user: locals.user,
		modalMode: true
	};
};

import { vi } from 'vitest';
import fs from 'fs/promises';
import path from 'path';

/**
 * Mock-Reality Drift Detection System
 *
 * Monitors changes in real implementations to detect when mocks
 * need updates to stay synchronized with actual code behavior.
 */

interface MockSignature {
	name: string;
	type: 'function' | 'class' | 'module';
	signature: string;
	lastUpdated: string;
	sourceFile: string;
	checksum: string;
}

interface DriftReport {
	timestamp: string;
	driftsDetected: DriftItem[];
	suggestions: string[];
	severity: 'low' | 'medium' | 'high' | 'critical';
}

interface DriftItem {
	mockName: string;
	sourceFile: string;
	type: 'signature_change' | 'new_method' | 'removed_method' | 'behavioral_change';
	description: string;
	impact: 'low' | 'medium' | 'high';
}

export class MockDriftDetector {
	private static instance: MockDriftDetector;
	private mockRegistry: Map<string, MockSignature> = new Map();
	private driftHistory: DriftReport[] = [];

	static getInstance(): MockDriftDetector {
		if (!MockDriftDetector.instance) {
			MockDriftDetector.instance = new MockDriftDetector();
		}
		return MockDriftDetector.instance;
	}

	/**
	 * Register a mock for drift detection
	 */
	registerMock(
		name: string,
		type: 'function' | 'class' | 'module',
		sourceFile: string,
		mockImplementation: unknown
	): void {
		const signature = this.generateSignature(mockImplementation, type);
		const checksum = this.generateChecksum(signature);

		this.mockRegistry.set(name, {
			name,
			type,
			signature,
			lastUpdated: new Date().toISOString(),
			sourceFile,
			checksum
		});
	}

	/**
	 * Check for drift in database-related mocks
	 */
	async checkDatabaseMockDrift(): Promise<DriftItem[]> {
		const drifts: DriftItem[] = [];

		try {
			// Check Prisma schema changes
			const schemaPath = path.resolve(process.cwd(), 'prisma/core.prisma');
			const schemaContent = await fs.readFile(schemaPath, 'utf-8');

			// Extract model definitions
			const models = this.extractPrismaModels(schemaContent);

			// Check if our database mock covers all models
			const registeredMock = this.mockRegistry.get('DatabaseMock');
			if (registeredMock) {
				const mockMethods = this.extractMockMethods(registeredMock.signature);
				const missingModels = models.filter((model) => !mockMethods.includes(model.toLowerCase()));

				if (missingModels.length > 0) {
					drifts.push({
						mockName: 'DatabaseMock',
						sourceFile: 'prisma/core.prisma',
						type: 'new_method',
						description: `Missing database mock methods for models: ${missingModels.join(', ')}`,
						impact: 'medium'
					});
				}
			}
		} catch (error) {
			console.warn('Unable to check database schema drift:', error);
		}

		return drifts;
	}

	/**
	 * Check for drift in API route handlers
	 */
	async checkApiRouteDrift(): Promise<DriftItem[]> {
		const drifts: DriftItem[] = [];

		try {
			// Check src/routes/api directory for new endpoints
			const apiDir = path.resolve(process.cwd(), 'src/routes/api');
			const endpoints = await this.scanApiEndpoints(apiDir);

			// Check if we have test coverage for all endpoints
			const testDir = path.resolve(process.cwd(), 'tests/integration');
			const testFiles = await fs.readdir(testDir);

			const untestedEndpoints = endpoints.filter((endpoint) => {
				const testFileName = `${endpoint.replace(/\//g, '-')}.test.ts`;
				return (
					!testFiles.includes(testFileName) &&
					!testFiles.some((file) => file.includes(endpoint.split('/')[0]))
				);
			});

			if (untestedEndpoints.length > 0) {
				drifts.push({
					mockName: 'API Coverage',
					sourceFile: 'src/routes/api',
					type: 'new_method',
					description: `Untested API endpoints detected: ${untestedEndpoints.join(', ')}`,
					impact: 'high'
				});
			}
		} catch (error) {
			console.warn('Unable to check API route drift:', error);
		}

		return drifts;
	}

	/**
	 * Check for drift in external service integrations
	 */
	checkServiceIntegrationDrift(): DriftItem[] {
		const drifts: DriftItem[] = [];

		// Check if OAuth providers match what we're mocking
		const expectedProviders = ['google', 'facebook', 'discord', 'linkedin', 'twitter'];
		const mockedProviders = Array.from(this.mockRegistry.keys())
			.filter((key) => key.toLowerCase().includes('oauth'))
			.map((key) => key.toLowerCase());

		const unmockedProviders = expectedProviders.filter(
			(provider) => !mockedProviders.some((mock) => mock.includes(provider))
		);

		if (unmockedProviders.length > 0) {
			drifts.push({
				mockName: 'OAuth Providers',
				sourceFile: 'src/lib/auth',
				type: 'new_method',
				description: `OAuth providers without mock coverage: ${unmockedProviders.join(', ')}`,
				impact: 'medium'
			});
		}

		return drifts;
	}

	/**
	 * Generate comprehensive drift report
	 */
	async generateDriftReport(): Promise<DriftReport> {
		const allDrifts: DriftItem[] = [
			...(await this.checkDatabaseMockDrift()),
			...(await this.checkApiRouteDrift()),
			...this.checkServiceIntegrationDrift()
		];

		const severity = this.calculateSeverity(allDrifts);
		const suggestions = this.generateSuggestions(allDrifts);

		const report: DriftReport = {
			timestamp: new Date().toISOString(),
			driftsDetected: allDrifts,
			suggestions,
			severity
		};

		this.driftHistory.push(report);

		// Keep only last 10 reports
		if (this.driftHistory.length > 10) {
			this.driftHistory = this.driftHistory.slice(-10);
		}

		return report;
	}

	/**
	 * Export drift detection results for CI
	 */
	async exportDriftReport(): Promise<void> {
		const report = await this.generateDriftReport();
		const outputPath = path.resolve(process.cwd(), 'coverage/mock-drift-report.json');

		try {
			await fs.mkdir(path.dirname(outputPath), { recursive: true });
			await fs.writeFile(outputPath, JSON.stringify(report, null, 2));
			console.log(`ðŸ“Š Mock drift report saved to: ${outputPath}`);
		} catch (error) {
			console.warn('Failed to save drift report:', error);
		}
	}

	/**
	 * Automated mock update suggestions
	 */
	generateMockUpdateCode(drifts: DriftItem[]): string {
		const suggestions: string[] = [];

		for (const drift of drifts) {
			switch (drift.type) {
				case 'new_method':
					if (drift.mockName === 'DatabaseMock') {
						suggestions.push(this.generateDatabaseMockUpdate(drift.description));
					}
					break;
				case 'signature_change':
					suggestions.push(`// Update ${drift.mockName} signature in ${drift.sourceFile}`);
					break;
			}
		}

		return suggestions.join('\n\n');
	}

	// Private helper methods

	private generateSignature(implementation: unknown, type: string): string {
		if (type === 'function') {
			return typeof implementation === 'function' ? implementation.toString() : 'unknown';
		} else if (type === 'class') {
			return typeof implementation === 'function' && implementation.prototype
				? Object.getOwnPropertyNames(implementation.prototype).join(',')
				: 'unknown';
		} else {
			return implementation && typeof implementation === 'object'
				? JSON.stringify(Object.keys(implementation).sort())
				: 'unknown';
		}
	}

	private generateChecksum(signature: string): string {
		// Simple checksum - in production, use a proper hashing algorithm
		return signature
			.split('')
			.reduce((acc, char) => acc + char.charCodeAt(0), 0)
			.toString(16);
	}

	private extractPrismaModels(schemaContent: string): string[] {
		const modelRegex = /model\s+(\w+)\s*{/g;
		const models: string[] = [];
		let match;

		while ((match = modelRegex.exec(schemaContent)) !== null) {
			models.push(match[1]);
		}

		return models;
	}

	private extractMockMethods(signature: string): string[] {
		// Extract method names from mock signature
		const methodRegex = /(\w+):/g;
		const methods: string[] = [];
		let match;

		while ((match = methodRegex.exec(signature)) !== null) {
			methods.push(match[1]);
		}

		return methods;
	}

	private async scanApiEndpoints(dir: string): Promise<string[]> {
		const endpoints: string[] = [];

		try {
			const entries = await fs.readdir(dir, { withFileTypes: true });

			for (const entry of entries) {
				if (entry.isDirectory()) {
					const subEndpoints = await this.scanApiEndpoints(path.join(dir, entry.name));
					endpoints.push(...subEndpoints.map((ep) => `${entry.name}/${ep}`));
				} else if (entry.name === '+server.ts' || entry.name === '+server.js') {
					const relativePath = path.relative(path.resolve(process.cwd(), 'src/routes/api'), dir);
					endpoints.push(relativePath || '/');
				}
			}
		} catch (error) {
			// Directory doesn't exist or is not accessible
		}

		return endpoints;
	}

	private calculateSeverity(drifts: DriftItem[]): 'low' | 'medium' | 'high' | 'critical' {
		if (drifts.length === 0) return 'low';

		const highImpactDrifts = drifts.filter((d) => d.impact === 'high').length;
		const mediumImpactDrifts = drifts.filter((d) => d.impact === 'medium').length;

		if (highImpactDrifts >= 3) return 'critical';
		if (highImpactDrifts >= 1) return 'high';
		if (mediumImpactDrifts >= 3) return 'medium';
		return 'low';
	}

	private generateSuggestions(drifts: DriftItem[]): string[] {
		const suggestions = new Set<string>();

		for (const drift of drifts) {
			switch (drift.type) {
				case 'new_method':
					suggestions.add('Review and update mock registry to include new methods');
					break;
				case 'signature_change':
					suggestions.add('Update mock signatures to match current implementations');
					break;
				case 'behavioral_change':
					suggestions.add('Verify mock behavior matches actual implementation');
					break;
			}
		}

		suggestions.add('Run tests after mock updates to ensure compatibility');
		suggestions.add('Consider adding integration tests for drift-detected areas');

		return Array.from(suggestions);
	}

	private generateDatabaseMockUpdate(description: string): string {
		const models = description.match(/models: (.+)$/)?.[1]?.split(', ') || [];

		return models
			.map(
				(model) => `
// Add to DatabaseMock interface:
${model.toLowerCase()}: {
	findUnique: ReturnType<typeof vi.fn>;
	findMany: ReturnType<typeof vi.fn>;
	create: ReturnType<typeof vi.fn>;
	update: ReturnType<typeof vi.fn>;
	delete: ReturnType<typeof vi.fn>;
};

// Add to createDatabaseMock():
${model.toLowerCase()}: {
	findUnique: vi.fn(),
	findMany: vi.fn(),
	create: vi.fn(),
	update: vi.fn(),
	delete: vi.fn()
},`
			)
			.join('\n');
	}
}

// Export for use in tests
export const mockDriftDetector = MockDriftDetector.getInstance();

import { afterAll, afterEach, beforeAll } from 'vitest';
import { mockDriftDetector } from './mock-drift-detection';
import fs from 'fs/promises';
import path from 'path';

/**
 * Test Monitoring and Health Tracking System
 *
 * Monitors test execution health, performance, and failure patterns
 * to provide insights for maintenance and optimization.
 */

interface TestMetrics {
	testName: string;
	suite: string;
	duration: number;
	status: 'passed' | 'failed' | 'skipped';
	timestamp: string;
	memoryUsage?: NodeJS.MemoryUsage;
	errorMessage?: string;
	retryCount?: number;
}

interface TestSuiteReport {
	timestamp: string;
	totalTests: number;
	passed: number;
	failed: number;
	skipped: number;
	totalDuration: number;
	averageDuration: number;
	slowestTests: TestMetrics[];
	failurePatterns: FailurePattern[];
	memoryLeaks: MemoryLeak[];
	recommendations: string[];
}

interface FailurePattern {
	pattern: string;
	frequency: number;
	affectedTests: string[];
	category: 'oauth' | 'database' | 'api' | 'environment' | 'timeout' | 'mock' | 'other';
}

interface MemoryLeak {
	testName: string;
	startMemory: number;
	endMemory: number;
	leakSize: number;
	severity: 'low' | 'medium' | 'high';
}

export class TestMonitor {
	private static instance: TestMonitor;
	private testMetrics: TestMetrics[] = [];
	private suiteStartTime: number = 0;
	private initialMemory: NodeJS.MemoryUsage | null = null;

	static getInstance(): TestMonitor {
		if (!TestMonitor.instance) {
			TestMonitor.instance = new TestMonitor();
		}
		return TestMonitor.instance;
	}

	/**
	 * Initialize monitoring for test suite
	 */
	startMonitoring(): void {
		this.suiteStartTime = Date.now();
		this.initialMemory = process.memoryUsage();
		this.testMetrics = [];
	}

	/**
	 * Record test execution metrics
	 */
	recordTest(
		testName: string,
		suite: string,
		duration: number,
		status: 'passed' | 'failed' | 'skipped',
		errorMessage?: string,
		retryCount?: number
	): void {
		this.testMetrics.push({
			testName,
			suite,
			duration,
			status,
			timestamp: new Date().toISOString(),
			memoryUsage: process.memoryUsage(),
			errorMessage,
			retryCount
		});
	}

	/**
	 * Analyze failure patterns to identify systemic issues
	 */
	analyzeFailurePatterns(): FailurePattern[] {
		const failures = this.testMetrics.filter((m) => m.status === 'failed');
		const patterns = new Map<string, FailurePattern>();

		for (const failure of failures) {
			if (!failure.errorMessage) continue;

			const category = this.categorizeFailure(failure.errorMessage);
			const pattern = this.extractFailurePattern(failure.errorMessage);

			if (!patterns.has(pattern)) {
				patterns.set(pattern, {
					pattern,
					frequency: 0,
					affectedTests: [],
					category
				});
			}

			const existing = patterns.get(pattern)!;
			existing.frequency++;
			existing.affectedTests.push(`${failure.suite} > ${failure.testName}`);
		}

		return Array.from(patterns.values())
			.filter((p) => p.frequency >= 2) // Only patterns affecting 2+ tests
			.sort((a, b) => b.frequency - a.frequency);
	}

	/**
	 * Detect potential memory leaks in tests
	 */
	detectMemoryLeaks(): MemoryLeak[] {
		const leaks: MemoryLeak[] = [];

		if (!this.initialMemory) return leaks;

		const memoryThreshold = 50 * 1024 * 1024; // 50MB threshold
		let previousMemory = this.initialMemory.heapUsed;

		for (const metric of this.testMetrics) {
			if (!metric.memoryUsage) continue;

			const currentMemory = metric.memoryUsage.heapUsed;
			const memoryDelta = currentMemory - previousMemory;

			if (memoryDelta > memoryThreshold) {
				leaks.push({
					testName: `${metric.suite} > ${metric.testName}`,
					startMemory: previousMemory,
					endMemory: currentMemory,
					leakSize: memoryDelta,
					severity:
						memoryDelta > 100 * 1024 * 1024
							? 'high'
							: memoryDelta > 75 * 1024 * 1024
								? 'medium'
								: 'low'
				});
			}

			previousMemory = currentMemory;
		}

		return leaks;
	}

	/**
	 * Generate comprehensive test health report
	 */
	generateHealthReport(): TestSuiteReport {
		const totalTests = this.testMetrics.length;
		const passed = this.testMetrics.filter((m) => m.status === 'passed').length;
		const failed = this.testMetrics.filter((m) => m.status === 'failed').length;
		const skipped = this.testMetrics.filter((m) => m.status === 'skipped').length;

		const totalDuration = this.testMetrics.reduce((sum, m) => sum + m.duration, 0);
		const averageDuration = totalTests > 0 ? totalDuration / totalTests : 0;

		const slowestTests = this.testMetrics
			.filter((m) => m.status === 'passed' || m.status === 'failed')
			.sort((a, b) => b.duration - a.duration)
			.slice(0, 10);

		const failurePatterns = this.analyzeFailurePatterns();
		const memoryLeaks = this.detectMemoryLeaks();
		const recommendations = this.generateRecommendations(
			failurePatterns,
			memoryLeaks,
			averageDuration
		);

		return {
			timestamp: new Date().toISOString(),
			totalTests,
			passed,
			failed,
			skipped,
			totalDuration,
			averageDuration,
			slowestTests,
			failurePatterns,
			memoryLeaks,
			recommendations
		};
	}

	/**
	 * Export monitoring results for CI analysis
	 */
	async exportHealthReport(): Promise<void> {
		const report = this.generateHealthReport();
		const outputPath = path.resolve(process.cwd(), 'coverage/test-health-report.json');

		try {
			await fs.mkdir(path.dirname(outputPath), { recursive: true });
			await fs.writeFile(outputPath, JSON.stringify(report, null, 2));

			// Also generate human-readable summary
			await this.exportHealthSummary(report);

			console.log(`ðŸ“Š Test health report saved to: ${outputPath}`);
		} catch (error) {
			console.warn('Failed to save test health report:', error);
		}
	}

	/**
	 * Generate actionable maintenance tasks
	 */
	generateMaintenanceTasks(): string[] {
		const tasks: string[] = [];
		const report = this.generateHealthReport();

		// High-frequency failure patterns
		const criticalPatterns = report.failurePatterns.filter((p) => p.frequency >= 5);
		if (criticalPatterns.length > 0) {
			tasks.push(
				`ðŸš¨ CRITICAL: Address high-frequency failure patterns: ${criticalPatterns.map((p) => p.category).join(', ')}`
			);
		}

		// Memory leaks
		const severleLeaks = report.memoryLeaks.filter((l) => l.severity === 'high');
		if (severleLeaks.length > 0) {
			tasks.push(
				`ðŸ§  MEMORY: Fix memory leaks in: ${severleLeaks.map((l) => l.testName).join(', ')}`
			);
		}

		// Slow tests
		const slowTests = report.slowestTests.filter((t) => t.duration > 5000);
		if (slowTests.length > 0) {
			tasks.push(
				`â±ï¸ PERFORMANCE: Optimize slow tests (>${5000 / 1000}s): ${slowTests.length} tests`
			);
		}

		// OAuth-specific issues
		const oauthIssues = report.failurePatterns.filter((p) => p.category === 'oauth');
		if (oauthIssues.length > 0) {
			tasks.push(`ðŸ” OAUTH: Review OAuth test configuration and mocks`);
		}

		// Mock drift
		tasks.push(`ðŸŽ­ MOCKS: Run mock drift detection weekly`);
		tasks.push(`ðŸ“Š MONITORING: Review test health reports monthly`);

		return tasks;
	}

	// Private helper methods

	private categorizeFailure(errorMessage: string): FailurePattern['category'] {
		const msg = errorMessage.toLowerCase();

		if (msg.includes('oauth') || msg.includes('authorization') || msg.includes('token')) {
			return 'oauth';
		}
		if (msg.includes('database') || msg.includes('prisma') || msg.includes('sql')) {
			return 'database';
		}
		if (msg.includes('timeout') || msg.includes('timed out')) {
			return 'timeout';
		}
		if (msg.includes('mock') || msg.includes('stub') || msg.includes('spy')) {
			return 'mock';
		}
		if (msg.includes('api') || msg.includes('request') || msg.includes('response')) {
			return 'api';
		}
		if (msg.includes('environment') || msg.includes('env') || msg.includes('variable')) {
			return 'environment';
		}

		return 'other';
	}

	private extractFailurePattern(errorMessage: string): string {
		// Simplify error messages to patterns
		return errorMessage
			.replace(/\d+/g, 'N') // Replace numbers with N
			.replace(/['"`][^'"`]*['"`]/g, 'STRING') // Replace strings
			.replace(/\/[^/\s]+/g, '/PATH') // Replace paths
			.substring(0, 100); // Limit length
	}

	private generateRecommendations(
		patterns: FailurePattern[],
		leaks: MemoryLeak[],
		avgDuration: number
	): string[] {
		const recommendations: string[] = [];

		// Failure pattern recommendations
		if (patterns.length > 0) {
			const topCategory = patterns[0].category;
			switch (topCategory) {
				case 'oauth':
					recommendations.push(
						'Review OAuth test setup and consider using more stable mock implementations'
					);
					break;
				case 'database':
					recommendations.push(
						'Check database connection stability and consider test isolation improvements'
					);
					break;
				case 'timeout':
					recommendations.push(
						'Increase test timeouts for CI environment or optimize slow operations'
					);
					break;
				case 'mock':
					recommendations.push('Review mock implementations for consistency with real services');
					break;
			}
		}

		// Memory recommendations
		if (leaks.length > 0) {
			recommendations.push('Implement proper cleanup in test teardown to prevent memory leaks');
			recommendations.push('Consider using beforeEach/afterEach for test isolation');
		}

		// Performance recommendations
		if (avgDuration > 2000) {
			recommendations.push('Consider parallelizing slow tests or optimizing test setup');
		}

		// General maintenance
		recommendations.push('Run mock drift detection regularly to catch API changes');
		recommendations.push('Monitor test health reports for trends and early warning signs');

		return recommendations;
	}

	private async exportHealthSummary(report: TestSuiteReport): Promise<void> {
		const summary = `# Test Health Summary

Generated: ${report.timestamp}

## Overview
- Total Tests: ${report.totalTests}
- Passed: ${report.passed} (${((report.passed / report.totalTests) * 100).toFixed(1)}%)
- Failed: ${report.failed}
- Skipped: ${report.skipped}
- Average Duration: ${report.averageDuration.toFixed(2)}ms

## Issues Found
${
	report.failurePatterns.length > 0
		? `
### Failure Patterns
${report.failurePatterns.map((p) => `- ${p.category.toUpperCase()}: ${p.pattern} (${p.frequency} occurrences)`).join('\n')}
`
		: 'âœ… No failure patterns detected'
}

${
	report.memoryLeaks.length > 0
		? `
### Memory Leaks
${report.memoryLeaks.map((l) => `- ${l.testName}: +${(l.leakSize / 1024 / 1024).toFixed(1)}MB (${l.severity})`).join('\n')}
`
		: 'âœ… No memory leaks detected'
}

## Maintenance Tasks
${this.generateMaintenanceTasks().join('\n')}

## Slowest Tests
${report.slowestTests
	.slice(0, 5)
	.map((t) => `- ${t.suite} > ${t.testName}: ${t.duration}ms`)
	.join('\n')}
`;

		const summaryPath = path.resolve(process.cwd(), 'coverage/test-health-summary.md');
		await fs.writeFile(summaryPath, summary);
	}
}

